      'preparing': 'IN_PROGRESS',
      'ready': 'READY',
      'completed': 'COMPLETED',
      'cancelled': 'CANCELED'
    };

    const squareState = statusMap[newStatus] || 'OPEN';

    console.log(`üîÑ Updating Square order ${squareOrderId} to state: ${squareState}`);

