  const isMobile = useIsMobile();
  const [selectedPackage, setSelectedPackage] = useState<typeof CREDIT_PACKAGES[0]>(CREDIT_PACKAGES[0]);
  const [loading, setLoading] = useState<boolean>(false);
  const [squareConfig, setSquareConfig] = useState<{
    applicationId: string;
    locationId: string;
  } | null>(null);
  const cardPaymentRef = useRef<HTMLDivElement>(null);
  const squarePaymentRef = useRef<any>(null);

  // Function to fetch Square configuration
  const fetchSquareConfig = useCallback(async () => {
    try {
      const response = await apiRequest('GET', '/api/square/config');
      const config = await response.json();
      console.log('Received Square config:', config);
      setSquareConfig(config);
      return config;
    } catch (error) {
      console.error('Failed to load Square configuration:', error);
      toast({
        title: 'Error',
        description: 'Failed to load payment configuration',
        variant: 'destructive',
      });
      return null;
    }
  }, [toast]);

  // Load Square configuration on component mount
  useEffect(() => {
    fetchSquareConfig();
  }, [fetchSquareConfig]);

  // Load Square Web Payments SDK
  useEffect(() => {
    if (!squareConfig?.applicationId || !squareConfig?.locationId || !cardPaymentRef.current) return;

    // Clean up function for component unmount
    let isComponentMounted = true;

    const setupSquare = async () => {
      // Check if the script is already loaded
      const scriptExists = document.querySelector('script[src*="square"]');

      if (!scriptExists) {
        // Create script element for Square SDK
        const script = document.createElement('script');

        // Always use sandbox for testing purposes
        script.src = 'https://sandbox.web.squarecdn.com/v1/square.js';

        console.log(`Loading Square SDK from: ${script.src} (Using Sandbox for testing)`);

        // Wait for script to load
        await new Promise((resolve, reject) => {
          script.onload = resolve;
          script.onerror = () => {
            reject(new Error('Failed to load Square SDK'));
            if (isComponentMounted) {
              toast({
                title: 'Error',
                description: 'Failed to load payment system',
                variant: 'destructive',
              });
            }
          };
          document.body.appendChild(script);
        });
      }

      // Initialize payment only if component is still mounted
      if (isComponentMounted && window.Square) {
        try {
          // Clean up previous instances
          if (squarePaymentRef.current) {
            try {
              await squarePaymentRef.current.destroy();
              squarePaymentRef.current = null;
            } catch (e) {
              console.error('Error cleaning up previous Square payment instance:', e);
            }
          }

          // Initialize Square payments
          const payments = window.Square.payments(squareConfig.applicationId, squareConfig.locationId);
          const card = await payments.card();
          await card.attach('#card-container');
          squarePaymentRef.current = card;
        } catch (error) {
          console.error('Failed to initialize Square payment:', error);
          if (isComponentMounted) {
            toast({
              title: 'Payment Setup Failed',
              description: 'Could not initialize the payment system',
              variant: 'destructive',
            });
          }
        }
      }
    };

    setupSquare();

    // Clean up function
    return () => {
      isComponentMounted = false;
      // Destroy card instance on unmount
      if (squarePaymentRef.current) {
        squarePaymentRef.current.destroy().catch((e: Error) => {
          console.error('Error destroying Square payment instance during cleanup:', e);
        });
      }
    };
  }, [squareConfig, toast, cardPaymentRef]);

  const handleSelectPackage = (pkg: typeof CREDIT_PACKAGES[0]) => {
    setSelectedPackage(pkg);
  };

  const handlePayment = async () => {
    if (!squarePaymentRef.current) {
      toast({
        title: 'Payment Error',
        description: 'Payment system not initialized',
        variant: 'destructive',
      });
      return;
    }

    setLoading(true);
    try {
      // Get payment token from Square
      const result = await squarePaymentRef.current.tokenize();
      if (result.status === 'OK') {
        // Process the payment with the token
        const response = await apiRequest('POST', '/api/square/process-payment', {
          sourceId: result.token,
          amount: selectedPackage.pay,
          bonusAmount: selectedPackage.receive,
          currency: 'AUD'
        });

        const paymentResult = await response.json();

        if (paymentResult.success) {
          toast({
            title: 'Payment Successful',
            description: `Successfully added ${selectedPackage.receive.toFixed(2)} credits to your account!`,
          });

          // Refresh user data to show updated credits
          queryClient.invalidateQueries({ queryKey: ['/api/user'] });
        } else {
          throw new Error(paymentResult.message || 'Payment processing failed');
        }
      } else {
        throw new Error(result.errors[0].message);
      }
    } catch (error) {
      console.error('Payment processing error:', error);
      toast({
        title: 'Payment Failed',
        description: error instanceof Error ? error.message : 'Failed to process payment',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="w-full max-h-[90vh] overflow-y-auto">
      <Card className="w-full max-w-md mx-auto">
        <CardHeader className="pb-2 pt-3">
          <CardTitle className="text-lg">Buy Credits</CardTitle>
          <CardDescription className="text-xs">
            {isMobile ? "Add credits to your account" : "Add credits to your account to place orders at Bean Stalker."}
          </CardDescription>
        </CardHeader>

        <CardContent className="space-y-3 py-2">
          <div className="space-y-3">
            <div>
              <h3 className="text-xs font-medium mb-1">Choose a Package</h3>
              <div className="grid gap-2 grid-cols-1">
                {CREDIT_PACKAGES.map((pkg) => (
                  <Button
                    key={pkg.pay}
                    type="button"
                    variant={selectedPackage.pay === pkg.pay ? 'default' : 'outline'}
                    className="w-full justify-between h-auto py-1.5 px-3 text-sm"
                    onClick={() => handleSelectPackage(pkg)}
                    disabled={loading}
                  >
                    <div className="flex flex-col items-start text-left">
                      <span className={`font-medium ${selectedPackage.pay === pkg.pay ? 'text-white' : ''}`}>
                        ${pkg.pay.toFixed(2)}
                      </span>
                      <span className={`text-xs ${selectedPackage.pay === pkg.pay ? 'text-white/80' : 'text-muted-foreground'}`}>
                        Pay
                      </span>
                    </div>

                    <div className="flex flex-col items-end text-right">
                      <span className={`font-bold ${selectedPackage.pay === pkg.pay ? 'text-white' : 'text-primary'}`}>
                        ${pkg.receive.toFixed(2)}
                      </span>
                      <span className={`text-xs ${selectedPackage.pay === pkg.pay ? 'text-white/80' : 'text-primary'}`}>
                        +{((pkg.receive/pkg.pay - 1) * 100).toFixed(1)}%
                      </span>
                    </div>
                  </Button>
                ))}
              </div>
            </div>

            <div>
              <h3 className="text-xs font-medium mb-1">Payment Details</h3>
              <div 
                id="card-container" 
                ref={cardPaymentRef} 
                className="min-h-[80px] mb-1 p-3 border rounded-md"
                style={{ fontSize: isMobile ? '13px' : '16px' }}
              ></div>
              <p className="text-xs text-muted-foreground">
                Securely processed by Square
              </p>
            </div>
          </div>
        </CardContent>

        <CardFooter className="pt-1 pb-3">
          <Button
            className="w-full"
            onClick={handlePayment}
            disabled={loading || !squarePaymentRef.current}
            size="sm"
          >
            {loading ? (
              <>
                <Loader2 className="h-3 w-3 mr-1 animate-spin" />
                Processing...
              </>
            ) : (
              <>
                <CreditCard className="h-3 w-3 mr-1" />
                {isMobile 
                  ? `Pay $${selectedPackage.pay} ‚Üí $${selectedPackage.receive.toFixed(2)}`
                  : `Pay $${selectedPackage.pay} for $${selectedPackage.receive.toFixed(2)} Credits`
                }
              </>
            )}
          </Button>
        </CardFooter>
      </Card>
    </div>
  );
}

client/src/components/cart-item.tsx.content:
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Trash2, Plus, Minus } from "lucide-react";
import { useCart } from "@/contexts/cart-context";
import { CartItem, CartItemOption } from "@shared/schema";
import { Badge } from "@/components/ui/badge";
import { motion, AnimatePresence } from "framer-motion";
import { useState } from "react";

interface CartItemCardProps {
  item: CartItem;
}

export function CartItemCard({ item }: CartItemCardProps) {
  const { updateCartItemQuantity, removeFromCart } = useCart();
  const [isRemoving, setIsRemoving] = useState(false);

  const handleIncrement = () => {
    updateCartItemQuantity(
      item.menuItemId, 
      item.quantity + 1, 
      item.size, 
      item.option, 
      item.options
    );
  };

  const handleDecrement = () => {
    if (item.quantity > 1) {
      updateCartItemQuantity(
        item.menuItemId, 
        item.quantity - 1, 
        item.size, 
        item.option, 
        item.options
      );
    } else {
      handleRemove();
    }
  };

  const handleRemove = () => {
    setIsRemoving(true);
    setTimeout(() => {
      removeFromCart(
        item.menuItemId, 
        item.size, 
        item.option, 
        item.options
      );
    }, 300);
  };

  // Group options by category/name for display
  const groupedOptions = (item.options || []).reduce<Record<string, string[]>>((acc, option) => {
    if (!acc[option.name]) {
      acc[option.name] = [];
    }
    acc[option.name].push(option.value);
    return acc;
  }, {});

  return (
    <motion.div
      layout
      initial={{ opacity: 0, y: 20 }}
      animate={{ 
        opacity: isRemoving ? 0 : 1, 
        x: isRemoving ? -100 : 0,
        height: isRemoving ? 0 : "auto"
      }}
      exit={{ opacity: 0, x: -100, height: 0 }}
      transition={{ duration: 0.3, ease: "easeInOut" }}
      className="border-b last:border-b-0 overflow-hidden"
    >
      <div className="flex py-4 px-1">
        {/* Item Image */}
        <motion.div 
          className="h-16 w-16 bg-gradient-to-br from-green-50 to-green-100 rounded-xl overflow-hidden mr-4 flex items-center justify-center shadow-sm"
          whileHover={{ scale: 1.05 }}
          transition={{ duration: 0.2 }}
        >
          {item.imageUrl ? (
            <img 
              src={item.imageUrl} 
              alt={item.name} 
              className="h-full w-full object-cover" 
            />
          ) : (
            <span className="text-green-600 font-semibold text-lg">
              {item.name[0]}
            </span>
          )}
        </motion.div>

        {/* Item Details */}
        <div className="flex-1 min-w-0">
          <div className="flex justify-between items-start mb-2">
            <div className="flex-1 min-w-0 pr-2">
              <h3 className="font-semibold text-gray-900 text-sm leading-tight">
                {item.name}
              </h3>

              {/* Size and Options */}
              <div className="flex flex-col gap-1 mt-1">
                {item.size && (
                  <div className="text-xs text-green-600 font-medium">
                    {item.size === 'small' ? 'Small' : item.size === 'medium' ? 'Medium' : 'Large'}
                  </div>
                )}

                {/* Legacy option field support */}
                {item.option && !item.options && (
                  <Badge variant="secondary" className="text-xs w-fit">
                    {item.option}
                  </Badge>
                )}

                {/* Display hierarchical options */}
                {item.options && item.options.length > 0 && (
                  <div className="flex flex-wrap gap-1">
                    {Object.entries(groupedOptions).map(([category, values], index) => (
                      <Badge key={index} variant="secondary" className="text-xs">
                        {category !== values[0] ? `${values[0]} ${category}` : values[0]}
                      </Badge>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* Price */}
            <div className="text-right">
              <div className="font-bold text-green-600 text-base">
                ${(item.price * item.quantity).toFixed(2)}
              </div>
              {item.quantity > 1 && (
                <div className="text-xs text-gray-500">
                  ${item.price.toFixed(2)} each
                </div>
              )}
            </div>
          </div>

          {/* Quantity Controls */}
          <div className="flex justify-between items-center">
            <div className="flex items-center bg-gray-50 rounded-full p-1">
              <motion.div
                whileTap={{ scale: 0.9 }}
              >
                <Button
                  variant="ghost"
                  size="icon"
                  className="h-8 w-8 rounded-full hover:bg-gray-200 transition-colors"
                  onClick={handleDecrement}
                >
                  <Minus className="h-3 w-3" />
                </Button>
              </motion.div>

              <motion.span 
                key={item.quantity}
                initial={{ scale: 1.2 }}
                animate={{ scale: 1 }}
                className="mx-3 font-semibold text-gray-900 min-w-[20px] text-center"
              >
                {item.quantity}
              </motion.span>

              <motion.div
                whileTap={{ scale: 0.9 }}
              >
                <Button
                  variant="ghost"
                  size="icon"
                  className="h-8 w-8 rounded-full hover:bg-gray-200 transition-colors"
                  onClick={handleIncrement}
                >
                  <Plus className="h-3 w-3" />
                </Button>
              </motion.div>
            </div>

            {/* Remove Button */}
            <motion.div
              whileTap={{ scale: 0.9 }}
            >
              <Button
                variant="ghost"
                size="icon"
                className="text-red-400 hover:text-red-600 hover:bg-red-50 h-8 w-8 rounded-full transition-colors"
                onClick={handleRemove}
              >
                <Trash2 className="h-4 w-4" />
              </Button>
            </motion.div>
          </div>
        </div>
      </div>
    </motion.div>
  );
}

client/src/components/cart-success-anima.content:
import { motion, AnimatePresence } from "framer-motion";
import { ShoppingBag, Check } from "lucide-react";
import { useEffect, useState } from "react";

interface CartSuccessAnimationProps {
  isVisible: boolean;
  onComplete: () => void;
  itemName?: string;
}

export function CartSuccessAnimation({ 
  isVisible, 
  onComplete, 
  itemName 
}: CartSuccessAnimationProps) {
  const [showCheck, setShowCheck] = useState(false);

  useEffect(() => {
    if (isVisible) {
      const timer = setTimeout(() => {
        setShowCheck(true);
      }, 500);

      const completeTimer = setTimeout(() => {
        onComplete();
        setShowCheck(false);
      }, 2000);

      return () => {
        clearTimeout(timer);
        clearTimeout(completeTimer);
      };
    }
  }, [isVisible, onComplete]);

  return (
    <AnimatePresence>
      {isVisible && (
        <motion.div
          initial={{ opacity: 0, scale: 0.5, y: 50 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          exit={{ opacity: 0, scale: 0.5, y: -50 }}
          transition={{ 
            type: "spring",
            duration: 0.6,
            bounce: 0.4
          }}
          className="fixed bottom-20 left-1/2 transform -translate-x-1/2 z-50"
        >
          <div className="bg-green-600 text-white px-6 py-4 rounded-full shadow-lg flex items-center space-x-3">
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: 0.2, type: "spring", bounce: 0.6 }}
              className="relative"
            >
              <ShoppingBag className="h-6 w-6" />
              <AnimatePresence>
                {showCheck && (
                  <motion.div
                    initial={{ scale: 0, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    exit={{ scale: 0, opacity: 0 }}
                    className="absolute -top-1 -right-1 bg-white text-green-600 rounded-full p-1"
                  >
                    <Check className="h-3 w-3" />
                  </motion.div>
                )}
              </AnimatePresence>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, x: -10 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.3 }}
            >
              <p className="font-semibold text-sm">
                {itemName ? `${itemName} added!` : "Item added to cart!"}
              </p>
            </motion.div>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

client/src/components/category-filter.ts.content:
import { useRef } from "react";
import { Badge } from "@/components/ui/badge";
import { ScrollArea, ScrollBar } from "@/components/ui/scroll-area";

interface CategoryFilterProps {
  categories: string[];
  selectedCategory: string;
  onSelectCategory: (category: string) => void;
}

export function CategoryFilter({ 
  categories, 
  selectedCategory, 
  onSelectCategory 
}: CategoryFilterProps) {
  const scrollRef = useRef<HTMLDivElement>(null);

  return (
    <ScrollArea className="w-full whitespace-nowrap mb-6">
      <div className="flex space-x-2 py-4" ref={scrollRef}>
        <Badge
          variant={selectedCategory === "all" ? "selectedCategory" : "unselectedCategory"}
          className="cursor-pointer px-4 py-2 text-sm rounded-full"
          onClick={() => onSelectCategory("all")}
        >
          All
        </Badge>

        {categories.map((category) => (
          <Badge
            key={category}
            variant={selectedCategory === category ? "selectedCategory" : "unselectedCategory"}
