          </Badge>
        ))}
      </div>
      <ScrollBar orientation="horizontal" />
    </ScrollArea>
  );
}

client/src/components/enhanced-buy-credi.content:
import { useState } from 'react';
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Capacitor } from '@capacitor/core';
import { useIAP } from '@/hooks/use-iap';

import { formatCurrency } from '@/lib/utils';
import { Smartphone, DollarSign } from 'lucide-react';

interface CreditPackage {
  id: string;
  amount: number;
  price: number;
  popular?: boolean;
  bonus?: number;
}

const CREDIT_PACKAGES: CreditPackage[] = [
  { id: 'com.beanstalker.credit25', amount: 25, price: 25, bonus: 4.50 }, // $25 ‚Üí $29.50
  { id: 'com.beanstalker.credit50', amount: 50, price: 50, bonus: 9.90, popular: true }, // $50 ‚Üí $59.90  
  { id: 'com.beanstalker.credit100', amount: 100, price: 100, bonus: 20.70 }, // $100 ‚Üí $120.70
  { id: 'com.beanstalker.membership69', amount: 0, price: 69, bonus: 0 }
];

export function EnhancedBuyCredits() {
  const [selectedPackage, setSelectedPackage] = useState<CreditPackage | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const { purchaseProduct, isAvailable: iapAvailable, isLoading: iapLoading } = useIAP();
  const isNative = Capacitor.isNativePlatform();

  const handlePurchase = async (creditPackage: CreditPackage) => {
    setIsProcessing(true);
    setSelectedPackage(creditPackage);

    try {
      // Process IAP purchase through App Store
      const result = await purchaseProduct(creditPackage.id);
      if (result.success) {
        console.log('App Store purchase successful:', result);
      }
    } catch (error) {
      console.error('App Store purchase failed:', error);
    } finally {
      setIsProcessing(false);
      setSelectedPackage(null);
    }
  };

  return (
    <div className="h-full max-h-[65vh] overflow-y-auto">
      <div className="space-y-4 p-1">
        {/* App Store Payment Header */}
        <div className="flex items-center gap-2 text-sm text-gray-600 px-2">
          <Smartphone className="h-4 w-4" />
          <span className="text-xs">App Store In-App Purchase</span>
        </div>

        {/* Credit Packages - Mobile Optimized */}
        <div className="space-y-3">
          {CREDIT_PACKAGES.map((pkg) => (
            <Card key={pkg.id} className={`relative cursor-pointer transition-all duration-200 ${
              pkg.popular ? 'ring-2 ring-green-500 shadow-lg' : 'hover:shadow-md'
            }`}>
              {pkg.popular && (
                <Badge className="absolute -top-2 left-1/2 transform -translate-x-1/2 bg-green-500 text-xs">
                  Popular
                </Badge>
              )}

              <CardContent className="p-4">
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-1">
                      <DollarSign className="h-4 w-4 text-green-600" />
                      <span className="font-semibold text-lg">
                        {pkg.id === 'com.beanstalker.membership69' ? 'Premium Membership' : `${pkg.amount + (pkg.bonus || 0)} Credits`}
                      </span>
                    </div>
                    {pkg.bonus && pkg.id !== 'com.beanstalker.membership69' && (
                      <p className="text-xs text-green-600 mb-2">
                        {formatCurrency(pkg.amount)} + {formatCurrency(pkg.bonus)} bonus = {formatCurrency(pkg.amount + pkg.bonus)}
                      </p>
                    )}
                    <div className="text-xl font-bold text-gray-900">
                      {formatCurrency(pkg.price)}
                    </div>
                  </div>

                  <Button
                    onClick={() => handlePurchase(pkg)}
                    disabled={isProcessing || (isNative && iapLoading)}
                    size="sm"
                    variant={pkg.popular ? "default" : "outline"}
                    className="ml-4"
                  >
                    {isProcessing && selectedPackage?.id === pkg.id ? (
                      <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                    ) : (
                      'Buy'
                    )}
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Payment Method Info */}
        <div className="text-xs text-gray-500 space-y-1 px-2 pb-4">
          <p>‚Ä¢ Secure payments processed by App Store</p>
          <p>‚Ä¢ Credits added immediately after purchase</p>
          <p>‚Ä¢ Credits never expire and can be used for any order</p>
        </div>
      </div>
    </div>
  );
}

client/src/components/favorites-list.tsx.content:
import { useAuth } from "@/hooks/use-auth";
import { useQuery, useQueryClient, useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { MenuItem } from "@shared/schema";
import { Loader2, ShoppingCart } from "lucide-react";
import { MenuItemCard } from "./menu-item";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/alert";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { useCart } from "@/contexts/cart-context";

export function FavoritesList() {
  const { user } = useAuth();
  const { addToCart } = useCart();
  const { toast } = useToast();

  const { data: favorites, isLoading, error } = useQuery<MenuItem[]>({
    queryKey: ['/api/favorites'],
    queryFn: async () => {
      if (!user) return [];
      const res = await apiRequest('GET', '/api/favorites');
      return await res.json();
    },
    enabled: !!user
  });

  // Function to add all favorites to cart
  const addAllToCart = () => {
    if (!favorites || favorites.length === 0) {
      toast({
        title: "No items to add",
        description: "You don't have any favorites to add to cart.",
        variant: "destructive",
      });
      return;
    }

    // Add each favorite item to cart
    favorites.forEach(item => {
      // Convert null to undefined for imageUrl to satisfy type requirements
      const cartItem = {
        menuItemId: item.id,
        name: item.name,
        price: item.price,
        quantity: 1
      };

      // Only add imageUrl if it exists
      if (item.imageUrl) {
        (cartItem as any).imageUrl = item.imageUrl;
      }

      addToCart(cartItem);
    });

    toast({
      title: "Added to cart",
      description: `${favorites.length} items added to your cart.`,
    });
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center py-10">
        <Loader2 className="h-8 w-8 animate-spin text-primary" />
      </div>
    );
  }

  if (error) {
    return (
      <Alert variant="destructive" className="my-4">
        <AlertTitle>Error</AlertTitle>
        <AlertDescription>
          Failed to load favorites. Please try again later.
        </AlertDescription>
      </Alert>
    );
  }

  if (!favorites || favorites.length === 0) {
    return (
      <div className="text-center py-8">
        <h3 className="text-lg font-medium mb-2">No favorites yet</h3>
        <p className="text-muted-foreground mb-4">
          You haven't added any items to your favorites.
        </p>
        <Link href="/menu">
          <a className="text-primary hover:underline">Browse the menu</a>
        </Link>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-xl font-semibold">{favorites.length} Favorite Item{favorites.length !== 1 ? 's' : ''}</h2>
        <Button 
          onClick={addAllToCart}
          className="flex items-center gap-2 bg-primary hover:bg-primary/90"
        >
          <ShoppingCart className="h-4 w-4" />
          <span>Add All to Cart</span>
        </Button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 my-4">
        {favorites.map((item) => (
          <MenuItemCard key={item.id} item={item} />
        ))}
      </div>
    </div>
  );
}

