import { Card, CardContent, CardFooter } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useCart } from "@/contexts/cart-context";
import { MenuItem, MenuItemOption, CartItemOption } from "@shared/schema";
import { Heart } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useState, useEffect } from "react";
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from "@/components/ui/accordion";

interface MenuItemCardProps {
  item: MenuItem;
}

// Define a type for options with hierarchical structure
interface OptionWithChildren extends MenuItemOption {
  children?: MenuItemOption[];
}

export function MenuItemCard({ item }: MenuItemCardProps) {
  const { addToCart } = useCart();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const [selectedSize, setSelectedSize] = useState<'small' | 'medium' | 'large'>('small');
  const [selectedOptions, setSelectedOptions] = useState<Record<string, string>>({});

  // Fetch options if the item has options
  const { data: flavorOptions } = useQuery<OptionWithChildren[]>({
    queryKey: ['/api/menu', item.id, 'options'],
    queryFn: async () => {
      if (!item.hasOptions) return [];
      try {
        const res = await apiRequest('GET', `/api/menu/${item.id}/options`);
        return await res.json();
      } catch (error) {
        console.error("Error fetching options:", error);
        return [];
      }
    },
    enabled: !!item.hasOptions // Only run if item has options
  });

  // Initialize options container, but don't select anything by default
  useEffect(() => {
    if (flavorOptions && flavorOptions.length > 0) {
      // Only initialize parent options that require a selection (dropdown menus)
      const initialOptions: Record<string, string> = {};

      // Do not set initial values for any options - user must explicitly select everything
      // This applies to both parent options and standalone flavor options

      // Add a "Flavor" key for standalone flavor options, but don't select any by default
      if (flavorOptions.filter(opt => !opt.isParent && !opt.parentId).length > 0) {
        initialOptions["Flavor"] = ""; // Empty string means no selection
      }

      setSelectedOptions(initialOptions);
    }
  }, [flavorOptions]);

  const { data: favoriteStatus } = useQuery({
    queryKey: ['/api/favorites', item.id],
    queryFn: async () => {
      if (!user) return { isFavorite: false };
      try {
        const res = await apiRequest('GET', `/api/favorites/${item.id}`);
        return await res.json();
      } catch (error) {
        return { isFavorite: false };
      }
    },
    enabled: !!user // Only run if user is logged in
  });

  const addFavoriteMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest('POST', '/api/favorites', { menuItemId: item.id });
      return res.json();
    },
    onSuccess: () => {
      // Invalidate relevant queries
      queryClient.invalidateQueries({ queryKey: ['/api/favorites'] });
      queryClient.invalidateQueries({ queryKey: ['/api/favorites', item.id] });
      toast({
        title: "Added to favorites",
        description: `${item.name} has been added to your favorites.`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to add favorite",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  const removeFavoriteMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest('DELETE', `/api/favorites/${item.id}`);
      return res.json();
    },
    onSuccess: () => {
      // Invalidate relevant queries
      queryClient.invalidateQueries({ queryKey: ['/api/favorites'] });
      queryClient.invalidateQueries({ queryKey: ['/api/favorites', item.id] });
      toast({
        title: "Removed from favorites",
        description: `${item.name} has been removed from your favorites.`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to remove favorite",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  const toggleFavorite = () => {
    if (!user) {
      toast({
        title: "Login required",
        description: "Please log in to add items to favorites.",
        variant: "destructive",
      });
      return;
    }

    if (favoriteStatus?.isFavorite) {
      removeFavoriteMutation.mutate();
    } else {
      addFavoriteMutation.mutate();
    }
  };

  // Get all selected options with their price adjustments
  const getSelectedOptionsWithPrices = (): CartItemOption[] => {
    if (!flavorOptions || flavorOptions.length === 0) return [];

    const result: CartItemOption[] = [];

    // Process parent-child selections
    flavorOptions.forEach(option => {
      if (option.isParent && option.children) {
        // Get the selected child option for this parent
        const selectedChildName = selectedOptions[option.name];
        if (selectedChildName) {
          const selectedChild = option.children.find(child => child.name === selectedChildName);
          if (selectedChild) {
            result.push({
              name: option.name, // Parent name as category
              value: selectedChild.name, // Child name as value
              priceAdjustment: selectedChild.priceAdjustment || 0
            });
          }
        }
      } else if (!option.parentId && !option.isParent) {
        // Handle standard flavor options (using the "Flavor" key now)
        if (selectedOptions["Flavor"] === option.name) {
          result.push({
            name: "Flavor",
            value: option.name,
            priceAdjustment: option.priceAdjustment || 0
          });
        }
      }
    });

    return result;
  };

  // Calculate total price adjustment from all selected options
  const getTotalOptionPriceAdjustment = (): number => {
    const selectedOptionsList = getSelectedOptionsWithPrices();
    return selectedOptionsList.reduce((total, opt) => total + opt.priceAdjustment, 0);
  };

  // Get the price based on selected size and all options
  const getPrice = (): number => {
    let basePrice = item.price;

    // Apply size pricing if applicable
    if (item.hasSizes) {
      switch (selectedSize) {
        case 'small': basePrice = item.price; break;
        case 'medium': basePrice = item.mediumPrice || item.price * 1.25; break;
        case 'large': basePrice = item.largePrice || item.price * 1.5; break;
      }
    }

    // Add all option price adjustments
    const optionAdjustments = getTotalOptionPriceAdjustment();

    return basePrice + optionAdjustments;
  };

  const handleAddToCart = () => {
    // Get all selected options
    const optionsList = getSelectedOptionsWithPrices();

    addToCart({
      menuItemId: item.id,
      name: item.name,
      price: getPrice(),
      quantity: 1,
      imageUrl: item.imageUrl || undefined,
      size: item.hasSizes ? selectedSize : undefined,
      options: optionsList
    });

    // Show confirmation message with selected options
    let message = `Added ${item.name}`;
    if (item.hasSizes) message += ` (${selectedSize})`;

    // Add option descriptions to the confirmation message
    if (optionsList.length > 0) {
      const optionText = optionsList.map(opt => `${opt.value}`).join(', ');
      message += ` with ${optionText}`;
    }

    toast({
      title: "Added to cart",
      description: message
    });
  };

  return (
    <Card className="overflow-hidden hover:shadow-lg transition-all duration-200 border-0 bg-white/90 backdrop-blur-sm">
      <div className="h-32 sm:h-36 w-full bg-muted relative overflow-hidden">
        {item.imageUrl ? (
          <img 
            src={item.imageUrl} 
            alt={item.name}
            className="w-full h-full object-cover object-center hover:scale-105 transition-transform duration-200"
          />
        ) : (
          <div className="h-full w-full bg-gradient-to-br from-green-100 to-green-200 flex items-center justify-center text-green-700">
            <span className="text-xs font-medium text-center px-2">No Image Available</span>
          </div>
        )}
        {user && (
          <button 
            onClick={toggleFavorite}
            className="absolute top-2 right-2 p-1.5 bg-white/80 rounded-full shadow-md hover:bg-white transition-colors"
            aria-label={favoriteStatus?.isFavorite ? "Remove from favorites" : "Add to favorites"}
          >
            <Heart 
              className={`h-5 w-5 ${favoriteStatus?.isFavorite ? 'fill-red-500 text-red-500' : 'text-gray-500'}`} 
            />
          </button>
        )}
      </div>
      <CardContent className="p-3">
        <h3 className="font-semibold text-sm leading-tight">{item.name}</h3>
        <p className="text-xs text-gray-600 mt-1 line-clamp-2">{item.description}</p>
        <div className="flex items-center justify-between mt-2">
          <span className="font-bold text-green-700 text-sm">
            ${getPrice().toFixed(2)}
          </span>
          {item.hasSizes && (
            <span className="text-xs text-gray-500">
              {selectedSize.charAt(0).toUpperCase() + selectedSize.slice(1)}
            </span>
          )}
        </div>
      </CardContent>
      <CardFooter className="flex flex-col p-3 pt-0 gap-2">
        {item.hasSizes && (
          <div className="w-full">
            <Select 
              value={selectedSize}
              onValueChange={(value) => setSelectedSize(value as 'small' | 'medium' | 'large')}
            >
              <SelectTrigger className="text-xs h-8">
                <SelectValue placeholder="Select Size" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="small">Small - ${item.price.toFixed(2)}</SelectItem>
                <SelectItem value="medium">Medium - ${(item.mediumPrice || item.price * 1.25).toFixed(2)}</SelectItem>
                <SelectItem value="large">Large - ${(item.largePrice || item.price * 1.5).toFixed(2)}</SelectItem>
              </SelectContent>
            </Select>
          </div>
        )}

        {item.hasOptions && flavorOptions && flavorOptions.length > 0 && (
          <div className="w-full space-y-2">
            {/* Parent options with children (like "Milk Alternatives") */}
            {flavorOptions.filter(opt => opt.isParent && opt.children && opt.children.length > 0).map((parentOption) => (
              <div key={parentOption.id}>
                <Select 
                  value={selectedOptions[parentOption.name] || ''} 
                  onValueChange={(value) => {
                    setSelectedOptions(prev => ({
                      ...prev,
                      [parentOption.name]: value
                    }));
                  }}
                >
                  <SelectTrigger className="text-xs h-8">
                    <SelectValue placeholder={`${parentOption.name} (Optional)`} />
                  </SelectTrigger>
                  <SelectContent>
                    {parentOption.children?.map((childOption) => (
                      <SelectItem key={childOption.id} value={childOption.name}>
                        {childOption.name}
                        {typeof childOption.priceAdjustment === 'number' && childOption.priceAdjustment > 0 && 
                          ` +$${childOption.priceAdjustment.toFixed(2)}`}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            ))}

            {/* Standard flavor options */}
            {flavorOptions.filter(opt => !opt.isParent && !opt.parentId).length > 0 && (
              <div>
                <Select 
                  value={selectedOptions["Flavor"] || ''} 
                  onValueChange={(value) => {
                    setSelectedOptions(prev => ({
                      ...prev,
                      "Flavor": value
                    }));
                  }}
                >
                  <SelectTrigger className="text-xs h-8">
                    <SelectValue placeholder="Choose Flavor (Optional)" />
                  </SelectTrigger>
                  <SelectContent>
                    {flavorOptions
                      .filter(opt => !opt.isParent && !opt.parentId)
                      .map((option) => (
                        <SelectItem key={option.id} value={option.name}>
                          {option.name}
                          {typeof option.priceAdjustment === 'number' && option.priceAdjustment > 0 && 
                            ` +$${option.priceAdjustment.toFixed(2)}`}
                        </SelectItem>
                      ))
                    }
                  </SelectContent>
                </Select>
              </div>
            )}
          </div>
        )}

        <Button 
          onClick={handleAddToCart} 
          className="w-full bg-gradient-to-r from-green-600 to-green-700 hover:from-green-700 hover:to-green-800 text-white text-xs py-2 h-8 rounded-md shadow-sm"
          disabled={false}
        >
          Add ${getPrice().toFixed(2)}
        </Button>
      </CardFooter>
    </Card>
  );
}

client/src/components/mobile-debug-displ.content:
import { useState, useEffect } from 'react';
import { Capacitor } from '@capacitor/core';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';

interface DebugLog {
  timestamp: string;
  type: 'api' | 'auth' | 'error' | 'info';
  message: string;
  details?: any;
}

export function MobileDebugDisplay() {
  const [debugLogs, setDebugLogs] = useState<DebugLog[]>([]);
  const [isVisible, setIsVisible] = useState(false);

  // Only show debug on mobile/native platforms
  const shouldShow = Capacitor.isNativePlatform();

  useEffect(() => {
    if (!shouldShow) return;

    // Override console methods to capture logs
    const originalLog = console.log;
    const originalError = console.error;

    console.log = (...args) => {
      originalLog(...args);

      const message = args.join(' ');
      if (message.includes('API Request') || message.includes('Login') || message.includes('Auth')) {
        addDebugLog('info', message, args[1]);
      }
    };

    console.error = (...args) => {
      originalError(...args);

      const message = args.join(' ');
      if (message.includes('API') || message.includes('Login') || message.includes('Auth')) {
        addDebugLog('error', message, args[1]);
      }
    };

    return () => {
      console.log = originalLog;
      console.error = originalError;
    };
  }, [shouldShow]);

  const addDebugLog = (type: DebugLog['type'], message: string, details?: any) => {
    const newLog: DebugLog = {
      timestamp: new Date().toLocaleTimeString(),
      type,
      message,
      details
    };

    setDebugLogs(prev => [...prev.slice(-9), newLog]); // Keep last 10 logs
  };

  if (!shouldShow) return null;

  return (
    <div className="fixed bottom-4 right-4 z-50">
      <Button
        variant="outline"
        size="sm"
        onClick={() => setIsVisible(!isVisible)}
        className="mb-2 bg-blue-500 text-white"
      >
        Debug {debugLogs.length > 0 && `(${debugLogs.length})`}
      </Button>

      {isVisible && (
        <Card className="w-80 max-h-96 overflow-hidden bg-black/90 text-white border-blue-500">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm flex justify-between items-center">
              Mobile Debug Logs
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={() => setDebugLogs([])}
                className="text-xs"
              >
                Clear
              </Button>
            </CardTitle>
          </CardHeader>
          <CardContent className="p-2 space-y-1 max-h-72 overflow-y-auto text-xs">
            {debugLogs.length === 0 ? (
              <p className="text-gray-400">No debug logs yet</p>
            ) : (
              debugLogs.map((log, index) => (
                <div key={index} className="border-b border-gray-700 pb-1">
                  <div className="flex items-center gap-2 mb-1">
                    <Badge 
                      variant={log.type === 'error' ? 'destructive' : 'secondary'}
                      className="text-xs"
                    >
                      {log.type}
                    </Badge>
                    <span className="text-gray-400">{log.timestamp}</span>
                  </div>
                  <p className="text-white">{log.message}</p>
                  {log.details && (
                    <pre className="text-xs text-gray-300 mt-1 whitespace-pre-wrap">
                      {JSON.stringify(log.details, null, 2)}
                    </pre>
                  )}
                </div>
              ))
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

// Export a function to manually add debug logs
export const addMobileDebugLog = (type: DebugLog['type'], message: string, details?: any) => {
  // This will be handled by the component's console override
  if (type === 'error') {
    console.error(message, details);
  } else {
    console.log(message, details);
  }
};

client/src/components/mobile-network-tes.content:
import { useState } from 'react';
import { Capacitor, CapacitorHttp } from '@capacitor/core';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';

export function MobileNetworkTest() {
  const [testResults, setTestResults] = useState<string[]>([]);
  const [isRunning, setIsRunning] = useState(false);

  const addResult = (message: string) => {
    setTestResults(prev => [...prev, `${new Date().toLocaleTimeString()}: ${message}`]);
  };

  const runNetworkTests = async () => {
    setIsRunning(true);
    setTestResults([]);

    addResult('Starting network connectivity tests...');

    // Test 1: Basic internet connectivity
    try {
      addResult('Testing basic internet connectivity...');
      let response;

      if (isNative) {
        // Try multiple reliable endpoints for mobile
        const testUrls = ['https://www.google.com', 'https://www.apple.com', 'https://httpbin.org/get'];
        let success = false;

        for (const testUrl of testUrls) {
          try {
            const nativeResponse = await CapacitorHttp.request({
              url: testUrl,
              method: 'GET',
              connectTimeout: 5000,
              readTimeout: 5000
            });
            response = { status: nativeResponse.status };
            success = true;
            break;
          } catch (urlError) {
            continue;
          }
        }

        if (!success) {
          throw new Error('All connectivity tests failed');
        }
      } else {
        response = await fetch('https://httpbin.org/get', {
          method: 'GET',
          signal: AbortSignal.timeout(10000)
        });
      }

      addResult(`‚úÖ Internet OK: ${response?.status || 'Unknown'}`);
    } catch (error) {
      addResult(`‚ö†Ô∏è Internet Limited: ${error instanceof Error ? error.message : 'Unknown error'} (Bean Stalker should still work)`);
    }

    // Test 2: Production server connectivity
    try {
      addResult('Testing production server...');
      let response;

      if (isNative) {
        const nativeResponse = await CapacitorHttp.request({
          url: 'https://member.beanstalker.com.au/api/menu',
          method: 'GET',
          headers: {
            'User-Agent': 'Bean Stalker Mobile Test',
            'Accept': 'application/json'
          },
          connectTimeout: 10000,
          readTimeout: 10000
        });
        response = { status: nativeResponse.status };
      } else {
        response = await fetch('https://member.beanstalker.com.au/api/menu', {
          method: 'GET',
          signal: AbortSignal.timeout(10000)
        });
      }

      addResult(`‚úÖ Server OK: ${response.status}`);
    } catch (error) {
      addResult(`‚ùå Server FAILED: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }

    // Test 3: Authentication endpoint
    try {
      addResult('Testing auth endpoint...');
      let response;

      if (isNative) {
        const nativeResponse = await CapacitorHttp.request({
          url: 'https://member.beanstalker.com.au/api/login',
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'User-Agent': 'Bean Stalker Mobile Test',
            'Accept': 'application/json'
          },
          data: JSON.stringify({ username: 'test', password: 'test' }),
          connectTimeout: 10000,
          readTimeout: 10000
        });
        response = { status: nativeResponse.status };
      } else {
        response = await fetch('https://member.beanstalker.com.au/api/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'User-Agent': 'Bean Stalker Mobile Test'
          },
          body: JSON.stringify({ username: 'test', password: 'test' }),
          signal: AbortSignal.timeout(10000)
        });
      }

      addResult(`‚úÖ Auth endpoint OK: ${response.status}`);
    } catch (error) {
      addResult(`‚ùå Auth endpoint FAILED: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }

    // Test 4: With credentials
    try {
      addResult('Testing with valid credentials...');

      if (isNative) {
        const nativeResponse = await CapacitorHttp.request({
          url: 'https://member.beanstalker.com.au/api/login',
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'User-Agent': 'Bean Stalker Mobile Test',
            'Accept': 'application/json'
          },
          data: JSON.stringify({ username: 'iamninz', password: 'password123' }),
          connectTimeout: 10000,
          readTimeout: 10000
        });

        if (nativeResponse.status === 200) {
          const data = typeof nativeResponse.data === 'string' ? JSON.parse(nativeResponse.data) : nativeResponse.data;
          addResult(`‚úÖ LOGIN SUCCESS: User ${data.username}, Credits $${data.credits}`);

          // Test session persistence with a follow-up request
          try {
            const sessionTest = await CapacitorHttp.request({
              url: 'https://member.beanstalker.com.au/api/user',
              method: 'GET',
              headers: {
                'User-Agent': 'Bean Stalker Mobile Test',
                'Accept': 'application/json'
              },
              connectTimeout: 5000,
              readTimeout: 5000
            });

            if (sessionTest.status === 200) {
              addResult(`‚úÖ SESSION PERSISTENCE: Authentication working properly`);
            } else {
              addResult(`‚ö†Ô∏è SESSION ISSUE: Status ${sessionTest.status} - cookies may not be persisting`);
            }
          } catch (sessionError) {
            addResult(`‚ö†Ô∏è SESSION TEST FAILED: ${sessionError instanceof Error ? sessionError.message : 'Unknown error'}`);
          }
        } else {
          addResult(`‚ùå Login FAILED: ${nativeResponse.status}`);
        }
      } else {
        const response = await fetch('https://member.beanstalker.com.au/api/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'User-Agent': 'Bean Stalker Mobile Test'
          },
          body: JSON.stringify({ username: 'iamninz', password: 'password123' }),
          credentials: 'include',
          signal: AbortSignal.timeout(10000)
        });

        if (response.ok) {
          const data = await response.json();
          addResult(`‚úÖ LOGIN SUCCESS: User ${data.username}, Credits $${data.credits}`);
        } else {
          addResult(`‚ùå Login FAILED: ${response.status} ${response.statusText}`);
        }
      }
    } catch (error) {
      addResult(`‚ùå Login ERROR: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }

    setIsRunning(false);
    addResult('Network tests completed.');
  };

  // Always show for testing purposes
  const isNative = Capacitor.isNativePlatform();

  return (
    <Card className="mx-4 my-2">
      <CardHeader>
        <CardTitle className="text-sm">
          Network Test {isNative ? '(Native)' : '(Web)'}
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-2">
        <Button 
          onClick={runNetworkTests} 
          disabled={isRunning}
          className="w-full"
          size="sm"
        >
          {isRunning ? 'Testing...' : 'Run Network Test'}
        </Button>

        {testResults.length > 0 && (
          <div className="bg-black/90 text-white p-2 rounded text-xs max-h-48 overflow-y-auto">
            {testResults.map((result, index) => (
              <div key={index} className="mb-1">
                {result}
              </div>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/product-detail-mod.content:
import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { X, Heart, Minus, Plus } from "lucide-react";
import { Button } from "@/components/ui/button";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { MenuItem, MenuItemOption, CartItemOption } from "@shared/schema";
import { useCart } from "@/contexts/cart-context";
import { useAuth } from "@/hooks/use-auth";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface ProductDetailModalProps {
  item: MenuItem | null;
  isOpen: boolean;
  onClose: () => void;
}

interface OptionWithChildren extends MenuItemOption {
  children?: MenuItemOption[];
}

export function ProductDetailModal({ item, isOpen, onClose }: ProductDetailModalProps) {
  const { addToCart } = useCart();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const [selectedSize, setSelectedSize] = useState<'small' | 'medium' | 'large'>('small');
  const [selectedOptions, setSelectedOptions] = useState<Record<string, string>>({});
  const [quantity, setQuantity] = useState(1);

  // Fetch options if the item has options
  const { data: flavorOptions } = useQuery<OptionWithChildren[]>({
    queryKey: ['/api/menu', item?.id, 'options'],
    queryFn: async () => {
      if (!item?.hasOptions) return [];
      try {
        const res = await apiRequest('GET', `/api/menu/${item.id}/options`);
        return await res.json();
      } catch (error) {
        console.error("Error fetching options:", error);
        return [];
      }
    },
    enabled: !!item?.hasOptions
  });

  // Fetch favorite status
  const { data: favoriteStatus } = useQuery({
    queryKey: ['/api/favorites', item?.id],
    queryFn: async () => {
      if (!user || !item) return { isFavorite: false };
      try {
        const res = await apiRequest('GET', `/api/favorites/${item.id}`);
        return await res.json();
      } catch (error) {
        return { isFavorite: false };
      }
    },
    enabled: !!user && !!item
  });

  // Reset state when modal opens/closes or item changes
  useEffect(() => {
    if (isOpen && item) {
      setSelectedSize('small');
      setQuantity(1);

      if (flavorOptions && flavorOptions.length > 0) {
        const initialOptions: Record<string, string> = {};

        // Add a "Flavor" key for standalone flavor options, but don't select any by default
        if (flavorOptions.filter(opt => !opt.isParent && !opt.parentId).length > 0) {
          initialOptions["Flavor"] = "";
        }

        setSelectedOptions(initialOptions);
      } else {
        setSelectedOptions({});
      }
    }
  }, [isOpen, item, flavorOptions]);

  const addFavoriteMutation = useMutation({
    mutationFn: async () => {
      if (!item) return;
      const res = await apiRequest('POST', '/api/favorites', { menuItemId: item.id });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/favorites'] });
      queryClient.invalidateQueries({ queryKey: ['/api/favorites', item?.id] });
      toast({
        title: "Added to favorites",
        description: `${item?.name} has been added to your favorites.`,
      });
    }
  });

  const removeFavoriteMutation = useMutation({
    mutationFn: async () => {
      if (!item) return;
      const res = await apiRequest('DELETE', `/api/favorites/${item.id}`);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/favorites'] });
      queryClient.invalidateQueries({ queryKey: ['/api/favorites', item?.id] });
      toast({
        title: "Removed from favorites",
        description: `${item?.name} has been removed from your favorites.`,
      });
    }
  });

  const toggleFavorite = () => {
    if (!user) {
      toast({
        title: "Login required",
        description: "Please log in to add items to favorites.",
        variant: "destructive",
      });
      return;
    }

    if (favoriteStatus?.isFavorite) {
      removeFavoriteMutation.mutate();
    } else {
      addFavoriteMutation.mutate();
    }
  };

  const getSelectedOptionsWithPrices = (): CartItemOption[] => {
    if (!flavorOptions || flavorOptions.length === 0) return [];

    const result: CartItemOption[] = [];

    flavorOptions.forEach(option => {
      if (option.isParent && option.children) {
        const selectedChildName = selectedOptions[option.name];
        if (selectedChildName) {
          const selectedChild = option.children.find(child => child.name === selectedChildName);
          if (selectedChild) {
            result.push({
              name: option.name,
              value: selectedChild.name,
              priceAdjustment: selectedChild.priceAdjustment || 0
            });
          }
        }
      } else if (!option.parentId && !option.isParent) {
        if (selectedOptions["Flavor"] === option.name) {
          result.push({
            name: "Flavor",
            value: option.name,
            priceAdjustment: option.priceAdjustment || 0
          });
        }
      }
    });

    return result;
  };

  const getTotalOptionPriceAdjustment = (): number => {
    const selectedOptionsList = getSelectedOptionsWithPrices();
    return selectedOptionsList.reduce((total, opt) => total + opt.priceAdjustment, 0);
  };

  const getPrice = (): number => {
    if (!item) return 0;

    let basePrice = item.price;

    if (item.hasSizes) {
      switch (selectedSize) {
        case 'small': basePrice = item.price; break;
        case 'medium': basePrice = item.mediumPrice || item.price * 1.25; break;
        case 'large': basePrice = item.largePrice || item.price * 1.5; break;
      }
    }

    const optionAdjustments = getTotalOptionPriceAdjustment();
    return basePrice + optionAdjustments;
  };

  const handleAddToCart = () => {
    if (!item) return;

    const optionsList = getSelectedOptionsWithPrices();

    for (let i = 0; i < quantity; i++) {
      addToCart({
        menuItemId: item.id,
        name: item.name,
        price: getPrice(),
        quantity: 1,
        imageUrl: item.imageUrl || undefined,
        size: item.hasSizes ? selectedSize : undefined,
        options: optionsList
      });
    }

    let message = `Added ${quantity}x ${item.name}`;
    if (item.hasSizes) message += ` (${selectedSize})`;

    if (optionsList.length > 0) {
      const optionText = optionsList.map(opt => `${opt.value}`).join(', ');
      message += ` with ${optionText}`;
    }

    toast({
      title: "Added to cart",
      description: message
    });

    onClose();
  };

  if (!item) return null;

  return (
    <AnimatePresence>
      {isOpen && (
        <div className="fixed inset-0 z-[100] flex items-end justify-center">
          {/* Backdrop */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="absolute inset-0 bg-black/50"
            onClick={onClose}
          />

          {/* Modal */}
          <motion.div
            initial={{ y: "100%" }}
            animate={{ y: 0 }}
            exit={{ y: "100%" }}
            transition={{ type: "spring", damping: 30, stiffness: 300 }}
            className="relative w-full max-w-md bg-white rounded-t-3xl shadow-2xl max-h-[85vh] overflow-y-auto"
          >
            {/* Header */}
            <div className="sticky top-0 bg-white/95 backdrop-blur-sm border-b border-gray-100 p-4 flex justify-between items-center rounded-t-3xl z-10">
              <h2 className="text-lg font-semibold text-gray-900">Product Details</h2>
              <Button
                variant="ghost"
                size="sm"
                onClick={onClose}
                className="p-2 h-auto"
              >
                <X className="h-5 w-5" />
              </Button>
            </div>

            {/* Content */}
            <div className="p-6 space-y-6 pb-24">
              {/* Product Image and Info */}
              <div className="relative z-0">
                <div className="aspect-video w-full bg-gray-100 rounded-2xl overflow-hidden mb-4">
                  {item.imageUrl ? (
                    <img 
                      src={item.imageUrl} 
                      alt={item.name}
                      className="w-full h-full object-cover"
                    />
                  ) : (
                    <div className="h-full w-full bg-gradient-to-br from-green-100 to-green-200 flex items-center justify-center text-green-700">
                      <span className="text-lg font-medium">No Image Available</span>
                    </div>
                  )}
                </div>

                {/* Heart Icon */}
                {user && (
                  <button 
                    onClick={toggleFavorite}
                    className="absolute top-4 right-4 p-2 bg-white/90 rounded-full shadow-md hover:bg-white transition-colors"
                  >
                    <Heart 
                      className={`h-5 w-5 ${favoriteStatus?.isFavorite ? 'fill-red-500 text-red-500' : 'text-gray-500'}`} 
                    />
                  </button>
                )}

                <div>
                  <h3 className="text-xl font-bold text-gray-900 mb-2">{item.name}</h3>
                  <p className="text-gray-600 text-sm mb-4">{item.description}</p>
                  <div className="flex items-center justify-between">
                    <span className="text-2xl font-bold text-green-600">
                      ${getPrice().toFixed(2)}
                    </span>
                    {(item.hasSizes || item.hasOptions) && (
                      <span className="text-sm text-gray-500 bg-gray-100 px-3 py-1 rounded-full">
                        Customizable
                      </span>
                    )}
                  </div>
                </div>
              </div>

              {/* Size Selection */}
              {item.hasSizes && (
                <Card>
                  <CardHeader className="pb-3">
                    <CardTitle className="text-base">Choose Size</CardTitle>
                  </CardHeader>
                  <CardContent className="pt-0">
                    <RadioGroup value={selectedSize} onValueChange={(value) => setSelectedSize(value as 'small' | 'medium' | 'large')}>
                      <div className="flex items-center space-x-2 p-3 border rounded-lg">
                        <RadioGroupItem value="small" id="small" />
                        <Label htmlFor="small" className="flex-1">
                          <div className="flex justify-between">
                            <span>Small</span>
                            <span className="font-semibold">${item.price.toFixed(2)}</span>
                          </div>
                        </Label>
                      </div>
                      <div className="flex items-center space-x-2 p-3 border rounded-lg">
                        <RadioGroupItem value="medium" id="medium" />
                        <Label htmlFor="medium" className="flex-1">
                          <div className="flex justify-between">
                            <span>Medium</span>
                            <span className="font-semibold">${(item.mediumPrice || item.price * 1.25).toFixed(2)}</span>
                          </div>
                        </Label>
                      </div>
                      <div className="flex items-center space-x-2 p-3 border rounded-lg">
                        <RadioGroupItem value="large" id="large" />
                        <Label htmlFor="large" className="flex-1">
                          <div className="flex justify-between">
                            <span>Large</span>
                            <span className="font-semibold">${(item.largePrice || item.price * 1.5).toFixed(2)}</span>
                          </div>
                        </Label>
                      </div>
                    </RadioGroup>
                  </CardContent>
                </Card>
              )}

              {/* Options Selection */}
              {item.hasOptions && flavorOptions && flavorOptions.length > 0 && (
                <Card>
                  <CardHeader className="pb-3">
                    <CardTitle className="text-base">Customize Your Order</CardTitle>
                  </CardHeader>
                  <CardContent className="pt-0 space-y-4">
                    {/* Parent options with children */}
                    {flavorOptions.filter(opt => opt.isParent && opt.children && opt.children.length > 0).map((parentOption) => (
                      <div key={parentOption.id}>
                        <Label className="text-sm font-medium text-gray-700 mb-2 block">
                          {parentOption.name} (Optional)
                        </Label>
                        <Select 
                          value={selectedOptions[parentOption.name] || ''} 
                          onValueChange={(value) => {
                            setSelectedOptions(prev => ({
                              ...prev,
                              [parentOption.name]: value
                            }));
                          }}
                        >
                          <SelectTrigger>
                            <SelectValue placeholder={`Choose ${parentOption.name}`} />
                          </SelectTrigger>
                          <SelectContent>
                            {parentOption.children?.map((childOption) => (
                              <SelectItem key={childOption.id} value={childOption.name}>
                                <div className="flex justify-between w-full">
                                  <span>{childOption.name}</span>
                                  {typeof childOption.priceAdjustment === 'number' && childOption.priceAdjustment > 0 && (
                                    <span className="ml-2">+${childOption.priceAdjustment.toFixed(2)}</span>
                                  )}
                                </div>
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                    ))}

                    {/* Standard flavor options */}
                    {flavorOptions.filter(opt => !opt.isParent && !opt.parentId).length > 0 && (
                      <div>
                        <Label className="text-sm font-medium text-gray-700 mb-2 block">
                          Flavor (Optional)
                        </Label>
                        <Select 
                          value={selectedOptions["Flavor"] || ''} 
                          onValueChange={(value) => {
                            setSelectedOptions(prev => ({
                              ...prev,
                              "Flavor": value
                            }));
                          }}
                        >
                          <SelectTrigger>
                            <SelectValue placeholder="Choose Flavor" />
                          </SelectTrigger>
                          <SelectContent>
                            {flavorOptions
                              .filter(opt => !opt.isParent && !opt.parentId)
                              .map((option) => (
                                <SelectItem key={option.id} value={option.name}>
                                  <div className="flex justify-between w-full">
                                    <span>{option.name}</span>
                                    {typeof option.priceAdjustment === 'number' && option.priceAdjustment > 0 && (
                                      <span className="ml-2">+${option.priceAdjustment.toFixed(2)}</span>
                                    )}
                                  </div>
                                </SelectItem>
                              ))
                            }
                          </SelectContent>
                        </Select>
                      </div>
                    )}
                  </CardContent>
                </Card>
              )}

              {/* Quantity Selection */}
              <Card>
                <CardHeader className="pb-3">
                  <CardTitle className="text-base">Quantity</CardTitle>
                </CardHeader>
                <CardContent className="pt-0">
                  <div className="flex items-center justify-between">
                    <span className="text-gray-700">How many would you like?</span>
                    <div className="flex items-center gap-3">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setQuantity(Math.max(1, quantity - 1))}
                        className="h-8 w-8 p-0"
                      >
                        <Minus className="h-4 w-4" />
                      </Button>
                      <span className="font-semibold text-lg w-8 text-center">{quantity}</span>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setQuantity(quantity + 1)}
                        className="h-8 w-8 p-0"
                      >
                        <Plus className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>

            </div>

            {/* Sticky Add to Cart Footer */}
            <div className="sticky bottom-0 bg-white/95 backdrop-blur-sm border-t border-gray-100 p-6 z-10">
              <Button 
                onClick={handleAddToCart} 
                className="w-full bg-gradient-to-r from-green-600 to-green-700 hover:from-green-700 hover:to-green-800 text-white py-3 h-auto rounded-xl shadow-lg"
              >
                Add to Cart ‚Ä¢ ${(getPrice() * quantity).toFixed(2)}
              </Button>
            </div>
          </motion.div>
        </div>
      )}
    </AnimatePresence>
  );
}

