import { useAuth } from "@/hooks/use-auth";
import { useQuery } from "@tanstack/react-query";
import { getQueryFn } from "@/lib/queryClient";
import { Loader2 } from "lucide-react";

// Define the response type for the QR code API
interface QRCodeResponse {
  qrCode: string;
}

export function QRCode() {
  const { user } = useAuth();
  const userId = user?.id || "";

  const { data, isLoading, error } = useQuery<QRCodeResponse>({
    queryKey: ["/api/user/qrcode"],
    queryFn: getQueryFn({ on401: "returnNull" }),
    enabled: !!userId,
  });

  // Show loading state
  if (isLoading) {
    return (
      <div className="w-full aspect-square flex items-center justify-center bg-white p-4 rounded-md">
        <Loader2 className="h-12 w-12 animate-spin text-primary" />
      </div>
    );
  }

  // Show error state
  if (error || !data?.qrCode) {
    return (
      <div className="w-full aspect-square flex items-center justify-center bg-white p-4 rounded-md">
        <div className="w-full aspect-square relative border-4 border-primary">
          <div className="border-4 border-primary absolute top-0 left-0 w-1/4 h-1/4 border-r-0 border-b-0"></div>
          <div className="border-4 border-primary absolute top-0 right-0 w-1/4 h-1/4 border-l-0 border-b-0"></div>
          <div className="border-4 border-primary absolute bottom-0 left-0 w-1/4 h-1/4 border-r-0 border-t-0"></div>
          <div className="border-4 border-primary absolute bottom-0 right-0 w-1/4 h-1/4 border-l-0 border-t-0"></div>
          <div className="absolute inset-0 flex items-center justify-center p-4 text-center">
            <div className="text-sm text-gray-600">Unable to load QR code. Please try again later.</div>
          </div>
          <div className="absolute -bottom-8 left-0 right-0 text-center text-xs">
            ID: {userId}
          </div>
        </div>
      </div>
    );
  }

  // Show the actual QR code
  return (
    <div className="w-full aspect-square flex items-center justify-center bg-white p-4 rounded-md">
      <div className="w-full aspect-square relative">
        <img 
          src={data.qrCode} 
          alt="User QR Code" 
          className="w-full h-full object-contain"
        />
        <div className="absolute -bottom-8 left-0 right-0 text-center text-xs">
          ID: {userId}
        </div>
      </div>
    </div>
  );
}

client/src/components/send-credits.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Switch } from "@/components/ui/switch";
import { useAuth } from "@/hooks/use-auth";
import { Loader2, Send, Phone, Check } from "lucide-react";
import { formatCurrency } from "@/lib/utils";

interface SendCreditsProps {
  open?: boolean;
  onOpenChange?: (open: boolean) => void;
}

export function SendCredits({ open: externalOpen, onOpenChange }: SendCreditsProps = {}) {
  const { toast } = useToast();
  const { user } = useAuth();
  const [internalOpen, setInternalOpen] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState<string>("");
  const [amount, setAmount] = useState<number>(0);
  const [message, setMessage] = useState<string>("");
  const [includeMessage, setIncludeMessage] = useState<boolean>(false);
  const [isSearching, setIsSearching] = useState<boolean>(false);
  const [recipientInfo, setRecipientInfo] = useState<{ id: number, username: string } | null>(null);

  // Use either controlled or uncontrolled open state
  const open = externalOpen !== undefined ? externalOpen : internalOpen;
  const setOpen = onOpenChange || setInternalOpen;

  // Mutation to look up a user by phone number
  const lookupUserMutation = useMutation({
    mutationFn: async (phoneNumber: string) => {
      const res = await apiRequest("GET", `/api/user/lookup?phoneNumber=${encodeURIComponent(phoneNumber)}`);
      return await res.json();
    },
    onSuccess: (data) => {
      if (data && data.id) {
        setRecipientInfo(data);
        toast({
          title: "User found",
          description: `Found user: ${data.username}`,
        });
      } else {
        setRecipientInfo(null);
        toast({
          title: "User not found",
          description: "No user with that phone number was found",
          variant: "destructive",
        });
      }
      setIsSearching(false);
    },
    onError: (error: Error) => {
      setRecipientInfo(null);
      toast({
        title: "Lookup failed",
        description: error.message,
        variant: "destructive",
      });
      setIsSearching(false);
    },
  });

  // Mutation to send credits
  const sendCreditsMutation = useMutation({
    mutationFn: async () => {
      if (!recipientInfo) throw new Error("No recipient selected");

      const data = {
        recipientId: recipientInfo.id,
        phoneNumber,
        amount,
        message: includeMessage ? message : undefined,
      };
      const res = await apiRequest("POST", "/api/send-credits", data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/credit-transactions"] });

      toast({
        title: "Credits sent successfully",
        description: `${formatCurrency(amount)} has been sent to ${recipientInfo?.username}.`,
      });

      // Reset form and close dialog
      setPhoneNumber("");
      setAmount(0);
      setMessage("");
      setRecipientInfo(null);
      setOpen(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to send credits",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handlePhoneNumberChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    // Format phone number as user types (optional)
    const value = e.target.value.replace(/\D/g, "");
    let formattedValue = value;

    if (value.length > 3 && value.length <= 6) {
      formattedValue = `${value.slice(0, 3)}-${value.slice(3)}`;
    } else if (value.length > 6) {
      formattedValue = `${value.slice(0, 3)}-${value.slice(3, 6)}-${value.slice(6, 10)}`;
    }

    setPhoneNumber(formattedValue);
    // Clear recipient info when phone number changes
    setRecipientInfo(null);
  };

  const handleLookupUser = () => {
    if (!phoneNumber || phoneNumber.replace(/\D/g, "").length < 10) {
      toast({
        title: "Invalid phone number",
        description: "Please enter a valid phone number",
        variant: "destructive",
      });
      return;
    }

    setIsSearching(true);
    lookupUserMutation.mutate(phoneNumber);
  };

  const handleSubmit = () => {
    if (!recipientInfo) {
      toast({
        title: "Missing recipient",
        description: "Please look up a valid phone number first",
        variant: "destructive",
      });
      return;
    }

    if (!amount || amount <= 0) {
      toast({
        title: "Invalid amount",
        description: "Please enter a valid amount greater than 0",
        variant: "destructive",
      });
      return;
    }

    if (amount > (user?.credits || 0)) {
      toast({
        title: "Insufficient credits",
        description: "You don't have enough credits to complete this transaction",
        variant: "destructive",
      });
      return;
    }

    sendCreditsMutation.mutate();
  };

  const handleOpenChange = (isOpen: boolean) => {
    setOpen(isOpen);
    if (!isOpen) {
      // Reset state when dialog is closed
      setPhoneNumber("");
      setAmount(0);
      setMessage("");
      setRecipientInfo(null);
    }
  };

  return (
    <Dialog open={open} onOpenChange={handleOpenChange}>
      <DialogTrigger asChild>
        <Button 
          variant="outline" 
          className="w-full flex items-center gap-2"
          onClick={() => setOpen(true)}
        >
          <Send className="h-4 w-4" />
          Send Credits
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Send Coffee Credits</DialogTitle>
          <DialogDescription>
            Send coffee credits to another Bean Stalker user via their phone number.
          </DialogDescription>
        </DialogHeader>
        <div className="grid gap-4 py-4">
          <div className="space-y-2">
            <div className="grid grid-cols-4 items-center gap-2">
              <Label htmlFor="phone-number" className="text-right">
                Phone Number
              </Label>
              <div className="col-span-3 flex gap-2">
                <Input
                  id="phone-number"
                  value={phoneNumber}
                  onChange={handlePhoneNumberChange}
                  className="flex-1"
                  placeholder="123-456-7890"
                />
                <Button 
                  onClick={handleLookupUser} 
                  variant="secondary"
                  size="icon"
                  disabled={isSearching}
                >
                  {isSearching ? (
                    <Loader2 className="h-4 w-4 animate-spin" />
                  ) : (
                    <Phone className="h-4 w-4" />
                  )}
                </Button>
              </div>
            </div>

            {recipientInfo && (
              <div className="ml-[25%] pl-2 text-sm font-medium text-green-600 flex items-center gap-1">
                <Check className="h-4 w-4" />
                <span>Recipient: {recipientInfo.username}</span>
              </div>
            )}
          </div>

          <div className="grid grid-cols-4 items-center gap-4">
            <Label htmlFor="amount" className="text-right">
              Amount
            </Label>
            <Input
              id="amount"
              type="number"
              min="0"
              step="0.01"
              value={amount || ""}
              onChange={(e) => setAmount(parseFloat(e.target.value) || 0)}
              className="col-span-3"
              placeholder="0.00"
            />
          </div>

          <div className="flex items-center gap-2">
            <Label htmlFor="include-message" className="text-right">
              Include Message
            </Label>
            <Switch 
              id="include-message"
              checked={includeMessage}
              onCheckedChange={setIncludeMessage}
            />
          </div>

          {includeMessage && (
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="message" className="text-right">
                Message
              </Label>
              <Textarea
                id="message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                className="col-span-3"
                placeholder="Add a personal message"
                rows={3}
              />
            </div>
          )}

          <div className="text-sm text-muted-foreground mt-2">
            Your current balance: {formatCurrency(user?.credits || 0)}
          </div>
        </div>
        <DialogFooter>
          <Button 
            type="submit" 
            onClick={handleSubmit}
            disabled={sendCreditsMutation.isPending || !recipientInfo}
          >
            {sendCreditsMutation.isPending ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Sending...
              </>
            ) : (
              "Send Credits"
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/transaction-histor.content:
import { useQuery } from '@tanstack/react-query';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { formatCurrency } from "@/lib/utils";
import { format } from 'date-fns';
import { CreditTransaction } from '@shared/schema';
import { useIsMobile } from '@/hooks/use-mobile';

export function TransactionHistory() {
  const isMobile = useIsMobile();
  const { data: transactions, isLoading, error } = useQuery<CreditTransaction[]>({
    queryKey: ['/api/credit-transactions'],
  });

  function getTransactionTypeLabel(type: string) {
    switch (type) {
      case 'purchase':
        return <Badge variant="default">Purchase</Badge>;
      case 'order':
        return <Badge variant="destructive">Order</Badge>;
      case 'admin':
        return <Badge variant="secondary">Admin</Badge>;
      default:
        return <Badge>{type}</Badge>;
    }
  }

  function formatAmount(amount: number) {
    if (amount > 0) {
      return <span className="text-green-600">+{formatCurrency(amount)}</span>;
    } else {
      return <span className="text-red-600">{formatCurrency(amount)}</span>;
    }
  }

  function formatDate(date: Date) {
    return isMobile
      ? format(date, 'MM/dd')
      : format(date, 'MMM d, yyyy HH:mm');
  }

  if (error) {
    return (
      <Card className="w-full">
        <CardHeader className="py-3">
          <CardTitle className="text-lg">Transaction History</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-red-500">
            Failed to load transactions: {(error as Error).message}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="w-full overflow-hidden">
      <CardHeader className="py-3">
        <CardTitle className="text-lg">Transaction History</CardTitle>
        <CardDescription>Recent credit activities</CardDescription>
      </CardHeader>
      <CardContent className="p-0">
        {isLoading ? (
          <div className="space-y-2 p-4">
            <Skeleton className="h-8 w-full" />
            <Skeleton className="h-8 w-full" />
            <Skeleton className="h-8 w-full" />
          </div>
        ) : transactions && transactions.length > 0 ? (
          <div className="max-h-[250px] overflow-auto">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead className="w-[80px]">Date</TableHead>
                  <TableHead className="w-[90px]">Type</TableHead>
                  {!isMobile && <TableHead>Description</TableHead>}
                  <TableHead className="text-right">Amount</TableHead>
                  {!isMobile && <TableHead className="text-right">Balance</TableHead>}
                </TableRow>
              </TableHeader>
              <TableBody>
                {[...transactions]
                  .sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
                  .map((transaction) => (
                  <TableRow key={transaction.id}>
                    <TableCell className="py-2">
                      {formatDate(new Date(transaction.createdAt))}
                    </TableCell>
                    <TableCell className="py-2">{getTransactionTypeLabel(transaction.type)}</TableCell>
                    {!isMobile && <TableCell className="py-2 truncate max-w-[120px]">{transaction.description}</TableCell>}
                    <TableCell className="py-2 text-right">{formatAmount(transaction.amount)}</TableCell>
                    {!isMobile && <TableCell className="py-2 text-right">{formatCurrency(transaction.balanceAfter)}</TableCell>}
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        ) : (
          <div className="text-center py-4 text-muted-foreground">
            No transactions found
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/contexts/app-update-context.t.content:
import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { useToast } from '@/hooks/use-toast';
import { Button } from '@/components/ui/button';
import { RefreshCw, Download } from 'lucide-react';

interface AppUpdateContextType {
  updateAvailable: boolean;
  installPromptEvent: any;
  checkForUpdates: () => Promise<boolean>;
  applyUpdate: () => void;
  showInstallPrompt: () => void;
}

const AppUpdateContext = createContext<AppUpdateContextType>({
  updateAvailable: false,
  installPromptEvent: null,
  checkForUpdates: async () => false,
  applyUpdate: () => {},
  showInstallPrompt: () => {},
});

export function AppUpdateProvider({ children }: { children: ReactNode }) {
  const [updateAvailable, setUpdateAvailable] = useState(false);
  const [waitingServiceWorker, setWaitingServiceWorker] = useState<ServiceWorker | null>(null);
  const [installPromptEvent, setInstallPromptEvent] = useState<any>(null);
  const { toast } = useToast();

  // Listen for beforeinstallprompt event to capture the install prompt
  useEffect(() => {
    const beforeInstallPromptHandler = (event: any) => {
      // Prevent the default prompt
      event.preventDefault();
      // Save the event for later use
      setInstallPromptEvent(event);
      console.log('Install prompt ready to use');
    };

    window.addEventListener('beforeinstallprompt', beforeInstallPromptHandler);

    return () => {
      window.removeEventListener('beforeinstallprompt', beforeInstallPromptHandler);
    };
  }, []);

  // Listen for service worker updates
  useEffect(() => {
    if ('serviceWorker' in navigator) {
      // Initial check when component mounts
      navigator.serviceWorker.ready.then((registration) => {
        console.log('Service worker ready:', registration.scope);
        checkForUpdates();
      });

      // Set up event listeners for service worker updates
      navigator.serviceWorker.addEventListener('controllerchange', () => {
        console.log('Service Worker controller changed');
      });

      // Listen for messages from service worker
      navigator.serviceWorker.addEventListener('message', (event) => {
        if (event.data && event.data.type === 'UPDATE_AVAILABLE') {
          console.log('Update message received from service worker');
          setUpdateAvailable(true);
          promptForUpdate();
        }
      });
    }
  }, []);

  // Function to check for updates
  const checkForUpdates = async (): Promise<boolean> => {
    if (!('serviceWorker' in navigator)) {
      return false;
    }

    try {
      const registration = await navigator.serviceWorker.ready;

      // Explicitly check for updates
      await registration.update();

      if (registration.waiting) {
        console.log('New service worker waiting');
        setWaitingServiceWorker(registration.waiting);
        setUpdateAvailable(true);
        promptForUpdate();
        return true;
      }

      return false;
    } catch (error) {
      console.error('Error checking for updates:', error);
      return false;
    }
  };

  // Function to apply update by telling the service worker to skip waiting
  const applyUpdate = () => {
    if (!waitingServiceWorker) {
      console.log('No waiting service worker found');
      return;
    }

    // Send message to service worker to skip waiting
    waitingServiceWorker.postMessage({ type: 'SKIP_WAITING' });

    // The page will reload automatically when the new service worker takes control
    toast({
      title: "Updating...",
      description: "The app will refresh in a moment.",
    });
  };

  // Show a toast notification with update prompt
  const promptForUpdate = () => {
    toast({
      title: "App Update Available",
      description: "A new version is available. Update now for the latest features and improvements.",
      action: (
        <Button 
          variant="outline" 
          size="sm" 
          onClick={applyUpdate}
          className="gap-1 items-center"
        >
          <RefreshCw className="h-4 w-4" />
          Update
        </Button>
      ),
      duration: 10000, // Show for 10 seconds
    });
  };

  // Function to show the install prompt
  const showInstallPrompt = async () => {
    if (!installPromptEvent) {
      console.log('No install prompt available');
      return;
    }

    try {
      // Show the prompt
      const result = await installPromptEvent.prompt();
      console.log('Install prompt result:', result);

      // Clear the saved prompt
      setInstallPromptEvent(null);
    } catch (error) {
      console.error('Error showing install prompt:', error);
    }
  };

  // Check for updates every 15 minutes
  useEffect(() => {
    const intervalId = setInterval(() => {
      console.log('Checking for updates...');
      checkForUpdates();
    }, 15 * 60 * 1000); // 15 minutes in milliseconds

    return () => clearInterval(intervalId);
  }, []);

  return (
    <AppUpdateContext.Provider 
      value={{ 
        updateAvailable, 
        installPromptEvent: !!installPromptEvent,
        checkForUpdates, 
        applyUpdate,
        showInstallPrompt
      }}
    >
      {children}
    </AppUpdateContext.Provider>
  );
}

export const useAppUpdate = () => useContext(AppUpdateContext);

