import { useState, useEffect } from 'react';
import { biometricService } from '@/services/biometric-service';
import { useAuth } from '@/hooks/use-auth';
import { useToast } from '@/hooks/use-toast';

export interface BiometricAuthState {
  isAvailable: boolean;
  biometricType: string;
  hasStoredCredentials: boolean;
  isLoading: boolean;
}

export function useBiometricAuth() {
  const { loginMutation } = useAuth();
  const { toast } = useToast();

  const [biometricState, setBiometricState] = useState<BiometricAuthState>({
    isAvailable: false,
    biometricType: 'unknown',
    hasStoredCredentials: false,
    isLoading: true,
  });

  // Check biometric availability on mount
  useEffect(() => {
    checkBiometricAvailability();
  }, []);

  const checkBiometricAvailability = async () => {
    try {
      setBiometricState(prev => ({ ...prev, isLoading: true }));

      const [isAvailable, biometricType, hasStoredCredentials] = await Promise.all([
        biometricService.isAvailable(),
        biometricService.getBiometricType(),
        biometricService.hasStoredCredentials(),
      ]);

      setBiometricState({
        isAvailable,
        biometricType,
        hasStoredCredentials,
        isLoading: false,
      });
    } catch (error) {
      console.error('Error checking biometric availability:', error);
      setBiometricState(prev => ({ 
        ...prev, 
        isLoading: false,
        isAvailable: false 
      }));
    }
  };

  const authenticateWithBiometrics = async (): Promise<boolean> => {
    try {
      if (!biometricState.isAvailable) {
        toast({
          title: "Biometric Authentication Unavailable",
          description: "Please use your username and password",
          variant: "destructive",
        });
        return false;
      }

      if (!biometricState.hasStoredCredentials) {
        toast({
          title: "No Biometric Login Set Up",
          description: "Sign in with password first to enable biometric login",
          variant: "destructive",
        });
        return false;
      }

      // Perform biometric authentication
      const credentials = await biometricService.authenticateWithBiometrics();

      if (credentials) {
        // Use existing login mutation with biometric credentials
        await loginMutation.mutateAsync({
          username: credentials.username,
          password: credentials.password,
        });

        const authType = getBiometricDisplayName(biometricState.biometricType);
        toast({
          title: "Authentication Successful",
          description: `Signed in with ${authType}`,
        });

        return true;
      }

      return false;
    } catch (error: any) {
      console.error('Biometric authentication failed:', error);

      let errorMessage = 'Authentication failed';
      if (error.message?.includes('User cancel')) {
        errorMessage = 'Authentication cancelled';
      } else if (error.message?.includes('not available')) {
        errorMessage = 'Biometric authentication not available';
      }

      toast({
        title: "Authentication Failed",
        description: errorMessage,
        variant: "destructive",
      });

      return false;
    }
  };

  const setupBiometricAuth = async (username: string, password: string): Promise<boolean> => {
    try {
      if (!biometricState.isAvailable) {
        return false;
      }

      const success = await biometricService.saveCredentials(username, password);

      if (success) {
        setBiometricState(prev => ({ 
          ...prev, 
          hasStoredCredentials: true 
        }));

        const authType = getBiometricDisplayName(biometricState.biometricType);
        toast({
          title: "Biometric Login Enabled",
          description: `You can now sign in with ${authType}`,
        });
      }

      return success;
    } catch (error) {
      console.error('Failed to setup biometric auth:', error);
      toast({
        title: "Setup Failed",
        description: "Could not enable biometric authentication",
        variant: "destructive",
      });
      return false;
    }
  };

  const disableBiometricAuth = async (): Promise<boolean> => {
    try {
      const success = await biometricService.deleteCredentials();

      if (success) {
        setBiometricState(prev => ({ 
          ...prev, 
          hasStoredCredentials: false 
        }));

        toast({
          title: "Biometric Login Disabled",
          description: "Biometric authentication has been turned off",
        });
      }

      return success;
    } catch (error) {
      console.error('Failed to disable biometric auth:', error);
      toast({
        title: "Error",
        description: "Could not disable biometric authentication",
        variant: "destructive",
      });
      return false;
    }
  };

  const getBiometricDisplayName = (type: string): string => {
    if (!type || typeof type !== 'string') {
      return 'Biometric authentication';
    }

    switch (type.toLowerCase()) {
      case 'faceid':
        return 'Face ID';
      case 'touchid':
        return 'Touch ID';
      case 'fingerprint':
        return 'Fingerprint';
      default:
        return 'Biometric authentication';
    }
  };

  const getBiometricIcon = (type: string): string => {
    if (!type || typeof type !== 'string') {
      return 'üîê'; // Generic biometric icon
    }

    switch (type.toLowerCase()) {
      case 'faceid':
        return 'üîí'; // Face ID icon
      case 'touchid':
        return 'üëÜ'; // Touch ID icon
      case 'fingerprint':
        return 'üëÜ'; // Fingerprint icon
      default:
        return 'üîê'; // Generic biometric icon
    }
  };

  return {
    biometricState,
    authenticateWithBiometrics,
    setupBiometricAuth,
    disableBiometricAuth,
    checkBiometricAvailability,
    getBiometricDisplayName,
    getBiometricIcon,
    isAuthenticating: loginMutation.isPending,
  };
}

client/src/hooks/use-iap.tsx.content:
import { createContext, useContext, useEffect, useState, ReactNode } from 'react';
import { iapService, IAPProduct, PurchaseResult } from '@/services/iap-service';
import { useAuth } from './use-auth';
import { useToast } from './use-toast';
import { apiRequest } from '@/lib/queryClient';

interface IAPContextType {
  isInitialized: boolean;
  products: IAPProduct[];
  isLoading: boolean;
  purchaseProduct: (productId: string) => Promise<PurchaseResult>;
  restorePurchases: () => Promise<boolean>;
  isAvailable: boolean;
}

const IAPContext = createContext<IAPContextType | null>(null);

export function IAPProvider({ children }: { children: ReactNode }) {
  const [isInitialized, setIsInitialized] = useState(false);
  const [products, setProducts] = useState<IAPProduct[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const { user } = useAuth();
  const { toast } = useToast();

  useEffect(() => {
    initializeIAP();
  }, []);

  useEffect(() => {
    if (user && isInitialized) {
      iapService.setUserID(user.id.toString());
    }
  }, [user, isInitialized]);

  const initializeIAP = async () => {
    setIsLoading(true);
    try {
      const initialized = await iapService.initialize();
      setIsInitialized(initialized);

      if (initialized) {
        const availableProducts = await iapService.getAvailableProducts();
        setProducts(availableProducts);
      }
    } catch (error) {
      console.error('Failed to initialize IAP:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const purchaseProduct = async (productId: string): Promise<PurchaseResult> => {
    if (!isInitialized) {
      toast({
        title: "Purchase Error",
        description: "Payment system not available",
        variant: "destructive",
      });
      return { success: false, productId, error: "Not initialized" };
    }

    setIsLoading(true);

    try {
      const result = await iapService.purchaseProduct(productId);

      if (result.success) {
        // Verify purchase with backend
        await verifyPurchase(result);

        toast({
          title: "Purchase Successful",
          description: "Your purchase has been completed successfully!",
        });
      } else {
        if (result.error !== 'Purchase cancelled by user') {
          toast({
            title: "Purchase Failed",
            description: result.error || "An error occurred during purchase",
            variant: "destructive",
          });
        }
      }

      return result;
    } catch (error: any) {
      toast({
        title: "Purchase Error",
        description: error.message || "An unexpected error occurred",
        variant: "destructive",
      });
      return { 
        success: false, 
        productId, 
        error: error.message || "Unexpected error" 
      };
    } finally {
      setIsLoading(false);
    }
  };

  const verifyPurchase = async (purchaseResult: PurchaseResult) => {
    try {
      const response = await apiRequest('POST', '/api/iap/verify-purchase', {
        productId: purchaseResult.productId,
        transactionId: purchaseResult.transactionId,
        receipt: purchaseResult.receipt,
        platform: 'ios' // Will be detected automatically
      });

      if (response.ok) {
        // Refresh user data to get updated credits/membership
        window.location.reload();
      }
    } catch (error) {
      console.error('Failed to verify purchase:', error);
      toast({
        title: "Verification Error",
        description: "Purchase successful but verification failed. Please contact support.",
        variant: "destructive",
      });
    }
  };

  const restorePurchases = async (): Promise<boolean> => {
    if (!isInitialized) return false;

    setIsLoading(true);
    try {
      const success = await iapService.restorePurchases();

      if (success) {
        toast({
          title: "Purchases Restored",
          description: "Your previous purchases have been restored successfully!",
        });
      } else {
        toast({
          title: "Restore Failed",
          description: "No previous purchases found to restore",
          variant: "destructive",
        });
      }

      return success;
    } catch (error: any) {
      toast({
        title: "Restore Error", 
        description: error.message || "Failed to restore purchases",
        variant: "destructive",
      });
      return false;
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <IAPContext.Provider
      value={{
        isInitialized,
        products,
        isLoading,
        purchaseProduct,
        restorePurchases,
        isAvailable: iapService.isAvailable(),
      }}
    >
      {children}
    </IAPContext.Provider>
  );
}

export function useIAP() {
  const context = useContext(IAPContext);
  if (!context) {
    throw new Error('useIAP must be used within an IAPProvider');
  }
  return context;
}

