                  {registerForm.watch('joinPremium') && (
                    <p className="text-xs text-gray-500 text-center">
                      Secure payment powered by Square. Your card will be charged AUD$69 and credited to your account immediately.
                    </p>
                  )}
                </form>
              </Form>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
}

