import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Coffee, CreditCard, Loader2, Eye, EyeOff } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

export default function AuthPage() {
  const { toast } = useToast();
  const [_, navigate] = useLocation();
  const [isLogin, setIsLogin] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [isLoginPending, setIsLoginPending] = useState(false);
  const [isRegisterPending, setIsRegisterPending] = useState(false);

  // Login form state
  const [loginData, setLoginData] = useState({
    username: "",
    password: "",
  });

  // Register form state
  const [registerData, setRegisterData] = useState({
    username: "",
    password: "",
    email: "",
    fullName: "",
    joinPremium: false,
  });

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoginPending(true);

    try {
      const response = await apiRequest("POST", "/api/login", loginData);
      if (response.ok) {
        const user = await response.json();
        toast({
          title: "Welcome back",
          description: `Signed in as ${user.username}`,
        });
        navigate('/');
      } else {
        throw new Error("Invalid credentials");
      }
    } catch (error) {
      toast({
        title: "Sign in failed",
        description: "Please check your credentials and try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoginPending(false);
    }
  };

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsRegisterPending(true);

    const { joinPremium, ...userData } = registerData;

    try {
      if (joinPremium) {
        toast({
          title: "Premium membership",
          description: "Processing premium membership for AUD$69...",
        });

        const response = await apiRequest("POST", "/api/register", {
          ...userData,
          credits: 69,
        });

        if (response.ok) {
          const user = await response.json();
          toast({
            title: "Premium membership activated",
            description: `Welcome ${user.username}! AUD$69 credit added to your account.`,
          });
          navigate('/');
        } else {
          throw new Error("Registration failed");
        }
      } else {
        const response = await apiRequest("POST", "/api/register", userData);
        if (response.ok) {
          const user = await response.json();
          toast({
            title: "Account created",
            description: `Welcome to Bean Stalker, ${user.username}!`,
          });
          navigate('/');
        } else {
          throw new Error("Registration failed");
        }
      }
    } catch (error) {
      toast({
        title: "Sign up failed",
        description: "Please check your information and try again.",
        variant: "destructive",
      });
    } finally {
      setIsRegisterPending(false);
    }
  };

  return (
    <div className="min-h-screen bg-black flex items-center justify-center p-4">
      <div className="w-full max-w-sm mx-auto">
        {/* Logo and Title */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <div className="w-12 h-12 bg-gradient-to-br from-green-400 to-green-600 rounded-xl flex items-center justify-center">
              <Coffee className="h-6 w-6 text-white" />
            </div>
          </div>
          <h1 className="text-2xl font-bold text-white mb-2">Bean Stalker</h1>
          <p className="text-gray-400 text-sm">
            {isLogin ? "Sign in to your account" : "Create your account"}
          </p>
        </div>

        {/* Auth Card */}
        <Card className="bg-gray-900 border-gray-800 shadow-2xl">
          <CardContent className="p-6">
            {isLogin ? (
              /* Login Form */
              <form onSubmit={handleLogin} className="space-y-4">
                <div className="space-y-3">
                  <Input
                    placeholder="Username"
                    value={loginData.username}
                    onChange={(e) => setLoginData({ ...loginData, username: e.target.value })}
                    className="bg-gray-800 border-gray-700 text-white placeholder-gray-500 focus:border-green-500 focus:ring-green-500"
                    required
                  />
                  <div className="relative">
                    <Input
                      type={showPassword ? "text" : "password"}
                      placeholder="Password"
                      value={loginData.password}
                      onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
                      className="bg-gray-800 border-gray-700 text-white placeholder-gray-500 focus:border-green-500 focus:ring-green-500 pr-10"
                      required
                    />
                    <button
                      type="button"
                      onClick={() => setShowPassword(!showPassword)}
                      className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500 hover:text-gray-300"
                    >
                      {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                    </button>
                  </div>
                </div>

                <Button
                  type="submit"
                  className="w-full bg-green-600 hover:bg-green-700 text-white font-medium py-2.5"
                  disabled={isLoginPending}
                >
                  {isLoginPending ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Signing in...
                    </>
                  ) : (
                    "Sign in"
                  )}
                </Button>
              </form>
            ) : (
              /* Register Form */
              <form onSubmit={handleRegister} className="space-y-4">
                <div className="space-y-3">
                  <Input
                    placeholder="Full name"
                    value={registerData.fullName}
                    onChange={(e) => setRegisterData({ ...registerData, fullName: e.target.value })}
                    className="bg-gray-800 border-gray-700 text-white placeholder-gray-500 focus:border-green-500 focus:ring-green-500"
                    required
                  />
                  <Input
                    type="email"
                    placeholder="Email"
                    value={registerData.email}
                    onChange={(e) => setRegisterData({ ...registerData, email: e.target.value })}
                    className="bg-gray-800 border-gray-700 text-white placeholder-gray-500 focus:border-green-500 focus:ring-green-500"
                    required
                  />
                  <Input
                    placeholder="Username"
                    value={registerData.username}
                    onChange={(e) => setRegisterData({ ...registerData, username: e.target.value })}
                    className="bg-gray-800 border-gray-700 text-white placeholder-gray-500 focus:border-green-500 focus:ring-green-500"
                    required
                  />
                  <div className="relative">
                    <Input
                      type={showPassword ? "text" : "password"}
                      placeholder="Password"
                      value={registerData.password}
                      onChange={(e) => setRegisterData({ ...registerData, password: e.target.value })}
                      className="bg-gray-800 border-gray-700 text-white placeholder-gray-500 focus:border-green-500 focus:ring-green-500 pr-10"
                      required
                    />
                    <button
                      type="button"
                      onClick={() => setShowPassword(!showPassword)}
                      className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500 hover:text-gray-300"
                    >
                      {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                    </button>
                  </div>
                </div>

                {/* Premium Membership Option */}
                <div className="flex items-start space-x-3 p-4 rounded-lg border border-gray-700 bg-gray-800/50">
                  <Checkbox
                    checked={registerData.joinPremium}
                    onCheckedChange={(checked) => setRegisterData({ ...registerData, joinPremium: Boolean(checked) })}
                    className="border-gray-600 data-[state=checked]:bg-green-600 data-[state=checked]:border-green-600"
                  />
                  <div className="flex-1">
                    <label className="flex items-center space-x-2 text-sm font-medium text-white cursor-pointer">
                      <CreditCard className="h-4 w-4 text-green-500" />
                      <span>Premium Membership - AUD$69</span>
                    </label>
                    <p className="text-xs text-gray-400 mt-1">
                      Get instant AUD$69 credit plus exclusive benefits and priority ordering.
                    </p>
                  </div>
                </div>

                <Button
                  type="submit"
                  className="w-full bg-green-600 hover:bg-green-700 text-white font-medium py-2.5"
                  disabled={isRegisterPending}
                >
                  {isRegisterPending ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Creating account...
                    </>
                  ) : registerData.joinPremium ? (
                    "Join Premium - AUD$69"
                  ) : (
                    "Create account"
                  )}
                </Button>
              </form>
            )}

            {/* Toggle between Login/Register */}
            <div className="mt-6 text-center">
              <button
                onClick={() => setIsLogin(!isLogin)}
                className="text-sm text-gray-400 hover:text-white transition-colors"
              >
                {isLogin ? (
                  <>Don't have an account? <span className="text-green-500 font-medium">Sign up</span></>
                ) : (
                  <>Already have an account? <span className="text-green-500 font-medium">Sign in</span></>
                )}
              </button>
            </div>
          </CardContent>
        </Card>

        {/* Footer */}
        <div className="text-center mt-8">
          <p className="text-xs text-gray-500">
            By continuing, you agree to our Terms of Service and Privacy Policy
          </p>
        </div>
      </div>
    </div>
  );
}

client/src/pages/auth-page.tsx.content:
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useAuth } from "@/hooks/use-auth";
import { z } from "zod";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogClose,
} from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Checkbox } from "@/components/ui/checkbox";
import { loadStripe } from "@stripe/stripe-js";
import { Elements, PaymentElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { MobileNetworkTest } from "@/components/mobile-network-test";

const loginSchema = z.object({
  username: z.string().min(1, "Username or Email is required"),
  password: z.string().min(1, "Password is required"),
});

const registerSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  email: z.string().email("Please enter a valid email address"),
  password: z.string()
    .min(8, "Password must be at least 8 characters")
    .regex(/[A-Z]/, "Password must contain at least one uppercase letter")
    .regex(/[a-z]/, "Password must contain at least one lowercase letter")
    .regex(/[0-9]/, "Password must contain at least one number"),
  confirmPassword: z.string(),
  fullName: z.string().optional(),
  phoneNumber: z.string().optional(),
  joinPremium: z.boolean().default(false),
}).refine(data => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

const forgotSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
});

const resetPasswordSchema = z.object({
  newPassword: z.string()
    .min(8, "Password must be at least 8 characters")
    .regex(/[A-Z]/, "Password must contain at least one uppercase letter")
    .regex(/[a-z]/, "Password must contain at least one lowercase letter")
    .regex(/[0-9]/, "Password must contain at least one number"),
  confirmPassword: z.string(),
}).refine(data => data.newPassword === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type LoginFormValues = z.infer<typeof loginSchema>;
type RegisterFormValues = z.infer<typeof registerSchema>;
type ForgotFormValues = z.infer<typeof forgotSchema>;
type ResetPasswordFormValues = z.infer<typeof resetPasswordSchema>;

export default function AuthPage() {
  const { user, loginMutation, registerMutation } = useAuth();
  const { toast } = useToast();
  const [_, navigate] = useLocation();
  const [forgotDialogOpen, setForgotDialogOpen] = useState(false);
  const [resetSent, setResetSent] = useState(false);
  const [searchParams] = useState(() => new URLSearchParams(window.location.search));
  const resetToken = searchParams.get('resetToken');
  const [activeTab, setActiveTab] = useState<'login' | 'register' | 'reset'>(resetToken ? 'reset' : 'login');

  const loginForm = useForm<LoginFormValues>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: "",
      password: "",
    },
  });

  const registerForm = useForm<RegisterFormValues>({
    resolver: zodResolver(registerSchema),
    defaultValues: {
      username: "",
      email: "",
      password: "",
      confirmPassword: "",
      fullName: "",
      phoneNumber: "",
      joinPremium: false,
    },
  });

  const forgotForm = useForm<ForgotFormValues>({
    resolver: zodResolver(forgotSchema),
    defaultValues: {
      email: "",
    },
  });

  const resetPasswordForm = useForm<ResetPasswordFormValues>({
    resolver: zodResolver(resetPasswordSchema),
    defaultValues: {
      newPassword: "",
      confirmPassword: "",
    },
  });

  useEffect(() => {
    // If user is already logged in, redirect to appropriate page
    if (user) {
      // Scroll to top before navigation
      window.scrollTo({ top: 0, behavior: 'smooth' });

      // If user is admin, redirect to admin page with orders tab
      if (user.isAdmin) {
        navigate("/admin");
      } else {
        // Otherwise redirect to home
        navigate("/");
      }
    }
  }, [user, navigate]);

  const onLoginSubmit = async (data: LoginFormValues) => {
    try {
      await login(data);
      // No need to navigate here as the useEffect will handle it
    } catch (error) {
      console.error("Login error:", error);
    }
  };

  // Password reset request mutation
  const resetRequestMutation = useMutation({
    mutationFn: async (email: string) => {
      const res = await apiRequest('POST', '/api/password-reset/request', { email });
      return await res.json();
    },
    onSuccess: () => {
      toast({
        title: "Password reset link sent",
        description: "If your email is registered, you will receive a password reset link shortly.",
      });
      setResetSent(true);
      forgotForm.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to send reset link. Please try again later.",
        variant: "destructive",
      });
    },
  });

  // Password reset mutation
  const resetPasswordMutation = useMutation({
    mutationFn: async (data: { token: string; newPassword: string }) => {
      const res = await apiRequest('POST', '/api/password-reset/reset', data);
      return await res.json();
    },
    onSuccess: () => {
      toast({
        title: "Password reset successful",
        description: "Your password has been reset. You can now log in with your new password.",
      });
      // Clear the token from URL
      window.history.replaceState({}, document.title, window.location.pathname);
      // Switch to login tab
      setActiveTab('login');
      resetPasswordForm.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to reset password. The link may be expired or invalid.",
        variant: "destructive",
      });
    },
  });

  const onForgotSubmit = async (data: ForgotFormValues) => {
    resetRequestMutation.mutate(data.email);
  };

  const onResetPasswordSubmit = async (data: ResetPasswordFormValues) => {
    if (!resetToken) {
      toast({
        title: "Error",
        description: "Reset token is missing. Please use the link from your email.",
        variant: "destructive",
      });
      return;
    }

    resetPasswordMutation.mutate({
      token: resetToken,
      newPassword: data.newPassword,
    });
  };

  return (
    <div className="min-h-screen bg-secondary flex flex-col items-center justify-center p-6">
      <div className="w-full max-w-md flex flex-col items-center mb-8">
        <img src="/images/bean-stalker-logo.png" alt="Bean Stalker Logo" className="w-40 h-auto" />
      </div>

      <Card className="w-full max-w-md">
        <CardHeader>
          <CardTitle className="text-center text-2xl text-[#124430]">
            Welcome to Bean Stalker
          </CardTitle>
          <CardDescription className="text-center">
            Order coffee and food from your favorite coffee shop
          </CardDescription>
        </CardHeader>
        <CardContent>
          {resetToken ? (
            <Tabs value={activeTab} onValueChange={(value) => setActiveTab(value as 'login' | 'reset')}>
              <TabsList className="grid w-full grid-cols-2 mb-4">
                <TabsTrigger value="login">Login</TabsTrigger>
                <TabsTrigger value="reset">Reset Password</TabsTrigger>
              </TabsList>

              <TabsContent value="login">
                <Form {...loginForm}>
                  <form
                    onSubmit={loginForm.handleSubmit(onLoginSubmit)}
                    className="space-y-4"
                  >
                    <FormField
                      control={loginForm.control}
                      name="username"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Username or Email</FormLabel>
                          <FormControl>
                            <Input
                              placeholder="Enter your username or email"
                              type="text"
                              {...field}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={loginForm.control}
                      name="password"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Password</FormLabel>
                          <FormControl>
                            <Input
                              placeholder="Password"
                              type="password"
                              {...field}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <Button
                      type="submit"
                      className="w-full bg-[#124430] hover:bg-[#0d3526] text-white"
                      disabled={isLoginPending}
                    >
                      {isLoginPending ? "Logging in..." : "LOG IN"}
                    </Button>

                    <div className="text-center mt-4">
                      <Dialog open={forgotDialogOpen} onOpenChange={setForgotDialogOpen}>
                        <DialogTrigger asChild>
                          <Button variant="link" className="text-[#124430]">
                            Forgot my User ID or Password
                          </Button>
                        </DialogTrigger>
                        <DialogContent className="sm:max-w-[425px]">
                          <DialogHeader>
                            <DialogTitle>Contact Bean Stalker</DialogTitle>
                            <DialogDescription>
                              Please contact Bean Stalker directly to request a password change.
                            </DialogDescription>
                          </DialogHeader>

                          <div className="py-6">
                            <div className="space-y-4 mb-6">
                              <p className="text-sm">
                                For security reasons, password resets must be handled by our staff. Please contact us using one of the methods below:
                              </p>

                              <div className="space-y-2">
                                <p className="text-sm font-medium">Phone:</p>
                                <p className="text-sm">0400 123 456</p>
                              </div>

                              <div className="space-y-2">
                                <p className="text-sm font-medium">Email:</p>
                                <p className="text-sm">info@beanstalker.com.au</p>
                              </div>

                              <p className="text-sm">
                                Our staff will verify your identity and assist you with resetting your password.
                              </p>
                            </div>

                            <DialogFooter>
                              <DialogClose asChild>
                                <Button variant="outline">
                                  Close
                                </Button>
                              </DialogClose>
                            </DialogFooter>
                          </div>
                        </DialogContent>
                      </Dialog>
                    </div>
                  </form>
                </Form>
              </TabsContent>

              <TabsContent value="reset">
                <Form {...resetPasswordForm}>
                  <form
                    onSubmit={resetPasswordForm.handleSubmit(onResetPasswordSubmit)}
                    className="space-y-4"
                  >
                    <div className="mb-4">
                      <h3 className="text-lg font-medium">Reset Your Password</h3>
                      <p className="text-sm text-muted-foreground">
                        Please enter a new password for your account.
                      </p>
                    </div>

                    <FormField
                      control={resetPasswordForm.control}
                      name="newPassword"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>New Password</FormLabel>
                          <FormControl>
                            <Input
                              placeholder="Enter your new password"
                              type="password"
                              {...field}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={resetPasswordForm.control}
                      name="confirmPassword"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Confirm Password</FormLabel>
                          <FormControl>
                            <Input
                              placeholder="Confirm your new password"
                              type="password"
                              {...field}
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <Button
                      type="submit"
                      className="w-full bg-[#124430] hover:bg-[#0d3526] text-white"
                      disabled={resetPasswordMutation.isPending}
                    >
                      {resetPasswordMutation.isPending ? "Resetting..." : "Reset Password"}
                    </Button>

                    <div className="text-center mt-4">
                      <Button 
                        variant="link" 
                        className="text-[#124430]"
                        onClick={() => setActiveTab('login')}
                      >
                        Back to Login
                      </Button>
                    </div>
                  </form>
                </Form>
              </TabsContent>
            </Tabs>
          ) : (
            // Default login form when no reset token is present
            <Form {...loginForm}>
              <form
                onSubmit={loginForm.handleSubmit(onLoginSubmit)}
                className="space-y-4"
              >
                <FormField
                  control={loginForm.control}
                  name="username"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Username or Email</FormLabel>
                      <FormControl>
                        <Input
                          placeholder="Enter your username or email"
                          type="text"
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={loginForm.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password</FormLabel>
                      <FormControl>
                        <Input
                          placeholder="Password"
                          type="password"
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <Button
                  type="submit"
                  className="w-full bg-[#124430] hover:bg-[#0d3526] text-white"
                  disabled={isLoginPending}
                >
                  {isLoginPending ? "Logging in..." : "LOG IN"}
                </Button>

                <div className="text-center mt-4">
                  <Dialog open={forgotDialogOpen} onOpenChange={setForgotDialogOpen}>
                    <DialogTrigger asChild>
                      <Button variant="link" className="text-[#124430]">
                        Forgot my User ID or Password
                      </Button>
                    </DialogTrigger>
                    <DialogContent className="sm:max-w-[425px]">
                      <DialogHeader>
                        <DialogTitle>Contact Bean Stalker</DialogTitle>
                        <DialogDescription>
                          Please contact Bean Stalker directly to request a password change.
                        </DialogDescription>
                      </DialogHeader>

                      <div className="py-6">
                        <div className="space-y-4 mb-6">
                          <p className="text-sm">
                            For security reasons, password resets must be handled by our staff. Please contact us using one of the methods below:
                          </p>

                          <div className="space-y-2">
                            <p className="text-sm font-medium">Phone:</p>
                            <p className="text-sm">0400 123 456</p>
                          </div>

                          <div className="space-y-2">
                            <p className="text-sm font-medium">Email:</p>
                            <p className="text-sm">info@beanstalker.com.au</p>
                          </div>

                          <p className="text-sm">
                            Our staff will verify your identity and assist you with resetting your password.
                          </p>
                        </div>

                        <DialogFooter>
                          <DialogClose asChild>
                            <Button variant="outline">
                              Close
                            </Button>
                          </DialogClose>
                        </DialogFooter>
                      </div>
                    </DialogContent>
                  </Dialog>
                </div>
              </form>
            </Form>
          )}
        </CardContent>
      </Card>

      <MobileNetworkTest />
    </div>
  );
}

client/src/pages/cart-page.tsx.content:
import { useState } from "react";
import { motion } from "framer-motion";
import { ArrowLeft, Minus, Plus, Trash2, ShoppingCart } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { useCart } from "@/contexts/cart-context";
import { useAuth } from "@/hooks/use-auth";
import { Link, useLocation } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { AppHeader } from "@/components/app-header";

export default function CartPage() {
  const { 
    cart, 
    removeFromCart, 
    updateCartItemQuantity, 
    clearCart, 
    calculateSubtotal, 
    calculateTotal 
  } = useCart();
  const { user } = useAuth();
  const [location, setLocation] = useLocation();
  const { toast } = useToast();
  const [isProcessing, setIsProcessing] = useState(false);

  const serviceFee = 0;

  const placeOrderMutation = useMutation({
    mutationFn: async (orderData: any) => {
      const response = await apiRequest("POST", "/api/orders", orderData);
      return await response.json();
    },
    onSuccess: (data) => {
      clearCart();
      toast({
        title: "Order Placed Successfully!",
        description: `Your order #${data.id} has been submitted to the kitchen.`,
      });
      setLocation("/orders");
    },
    onError: (error: Error) => {
      toast({
        title: "Order Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handlePlaceOrder = async () => {
    if (!user) {
      toast({
        title: "Please log in",
        description: "You need to be logged in to place an order.",
        variant: "destructive",
      });
      return;
    }

    if (cart.length === 0) {
      toast({
        title: "Cart is empty",
        description: "Add some items to your cart first.",
        variant: "destructive",
      });
      return;
    }

    const total = calculateTotal();

    if (user.credits < total) {
      toast({
        title: "Insufficient credits",
        description: `You need $${total.toFixed(2)} in credits but only have $${user.credits.toFixed(2)}.`,
        variant: "destructive",
      });
      return;
    }

    setIsProcessing(true);

    try {
      await placeOrderMutation.mutateAsync({
        items: cart.map(item => ({
          menuItemId: item.menuItemId,
          name: item.name,
          price: item.price,
          quantity: item.quantity,
          size: item.size,
          options: item.options
        })),
        total: total
      });
    } finally {
      setIsProcessing(false);
    }
  };

  const formatOptionDisplay = (options?: any[]) => {
    if (!options || options.length === 0) return '';
    return options.map(opt => opt.value).join(', ');
  };

  const handleQuantityChange = (item: any, newQuantity: number) => {
    if (newQuantity <= 0) {
      removeFromCart(item.menuItemId, item.size, undefined, item.options);
    } else {
      updateCartItemQuantity(item.menuItemId, newQuantity, item.size, undefined, item.options);
    }
  };

  const handleRemoveItem = (item: any) => {
    removeFromCart(item.menuItemId, item.size, undefined, item.options);
  };

  if (cart.length === 0) {
    return (
      <div className="min-h-screen flex flex-col bg-gradient-to-br from-slate-50 via-gray-50 to-green-50/30">
        <AppHeader />

        <div className="flex-1 flex items-center justify-center p-6">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="text-center"
          >
            <div className="w-24 h-24 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-6">
              <ShoppingCart className="h-12 w-12 text-gray-400" />
            </div>
            <h2 className="text-2xl font-bold text-gray-900 mb-2">Your cart is empty</h2>
            <p className="text-gray-600 mb-6">Looks like you haven't added any items to your cart yet.</p>
            <Link href="/menu">
              <Button className="bg-gradient-to-r from-green-600 to-green-700 hover:from-green-700 hover:to-green-800">
                Browse Menu
              </Button>
            </Link>
          </motion.div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-slate-50 via-gray-50 to-green-50/30">
      <AppHeader />

      <div className="flex-1 overflow-y-auto">
        <main className="p-4 max-w-2xl mx-auto pb-24">
          {/* Header */}
          <div className="flex items-center gap-4 mb-6">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setLocation("/menu")}
              className="p-2 h-auto"
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Your Cart</h1>
              <p className="text-sm text-gray-600">{cart.length} item{cart.length !== 1 ? 's' : ''}</p>
            </div>
          </div>

          {/* Cart Items */}
          <div className="space-y-4 mb-6">
            {cart.map((item, index) => (
              <motion.div
                key={`${item.menuItemId}-${item.size}-${JSON.stringify(item.options)}`}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.1 }}
              >
                <Card className="overflow-hidden border-gray-200">
                  <CardContent className="p-4">
                    <div className="flex gap-4">
                      {/* Item Image */}
                      <div className="w-16 h-16 bg-gray-100 rounded-lg overflow-hidden flex-shrink-0">
                        {item.imageUrl ? (
                          <img 
                            src={item.imageUrl} 
                            alt={item.name}
                            className="w-full h-full object-cover"
                          />
                        ) : (
                          <div className="w-full h-full bg-gradient-to-br from-green-100 to-green-200 flex items-center justify-center">
                            <span className="text-xs text-green-700 font-medium">No Image</span>
                          </div>
                        )}
                      </div>

                      {/* Item Details */}
                      <div className="flex-1 min-w-0">
                        <h3 className="font-semibold text-gray-900 text-sm mb-1">{item.name}</h3>

                        {/* Size and Options */}
                        <div className="space-y-1">
                          {item.size && (
                            <p className="text-xs text-gray-600">
                              Size: {item.size.charAt(0).toUpperCase() + item.size.slice(1)}
                            </p>
                          )}
                          {item.options && item.options.length > 0 && (
                            <p className="text-xs text-gray-600">
                              {formatOptionDisplay(item.options)}
                            </p>
                          )}
                        </div>

                        {/* Price and Quantity Controls */}
                        <div className="flex items-center justify-between mt-3">
                          <span className="font-semibold text-green-600">
                            ${item.price.toFixed(2)}
                          </span>

                          <div className="flex items-center gap-3">
                            {/* Remove Button */}
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleRemoveItem(item)}
                              className="p-1 h-auto text-gray-400 hover:text-red-500"
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>

                            {/* Quantity Controls */}
                            <div className="flex items-center gap-2">
                              <Button
                                variant="outline"
                                size="sm"
                                onClick={() => handleQuantityChange(item, item.quantity - 1)}
                                className="h-8 w-8 p-0"
                              >
                                <Minus className="h-3 w-3" />
                              </Button>
                              <span className="font-semibold text-sm w-8 text-center">
                                {item.quantity}
                              </span>
                              <Button
                                variant="outline"
                                size="sm"
                                onClick={() => handleQuantityChange(item, item.quantity + 1)}
                                className="h-8 w-8 p-0"
                              >
                                <Plus className="h-3 w-3" />
                              </Button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </motion.div>
            ))}
          </div>

          {/* Order Summary */}
          <Card className="border-gray-200 mb-6">
            <CardContent className="p-6">
              <h3 className="font-semibold text-gray-900 mb-4">Order Summary</h3>

              <div className="space-y-2 text-sm">
                <div className="flex justify-between">
                  <span className="text-gray-600">Subtotal</span>
                  <span className="text-gray-900">${calculateSubtotal().toFixed(2)}</span>
                </div>
                <div className="border-t border-gray-200 pt-2 mt-3">
                  <div className="flex justify-between font-semibold text-base">
                    <span className="text-gray-900">Total</span>
                    <span className="text-green-600">${calculateTotal().toFixed(2)}</span>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Clear Cart Button */}
          <div className="mb-6">
            <Button
              variant="outline"
              onClick={clearCart}
              className="w-full border-red-200 text-red-600 hover:bg-red-50"
            >
              Clear Cart
            </Button>
          </div>
        </main>

        {/* Sticky Footer - Place Order */}
        <div className="fixed bottom-0 left-0 right-0 bg-white/95 backdrop-blur-sm border-t border-gray-200 p-4 z-[60]">
          <div className="max-w-2xl mx-auto">
            <Button
              onClick={handlePlaceOrder}
              disabled={isProcessing || !user || cart.length === 0}
              className="w-full bg-gradient-to-r from-green-600 to-green-700 hover:from-green-700 hover:to-green-800 text-white py-3 h-auto rounded-xl shadow-lg"
            >
              {isProcessing ? (
                "Processing..."
              ) : (
                `Place Order ‚Ä¢ $${calculateTotal().toFixed(2)}`
              )}
            </Button>

            {user && (
              <div className="text-center mt-2 text-sm text-gray-600">
                Available Credits: ${user.credits.toFixed(2)}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

