import { useCallback } from "react";
import { AppHeader } from "@/components/app-header";

import { useQuery, useQueryClient } from "@tanstack/react-query";
import { Order, CartItem } from "@shared/schema";
import { formatCurrency, formatDate } from "@/lib/utils";
import { Loader2, RefreshCw } from "lucide-react";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";

export default function OrdersPage() {
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const {
    data: orders = [],
    isLoading,
    error,
    isRefetching,
  } = useQuery<Order[], Error>({
    queryKey: ["/api/orders"],
    refetchOnWindowFocus: true,
    refetchOnMount: true,
  });

  const sortedOrders = [...orders].sort(
    (a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
  );

  const getStatusColor = (status: string) => {
    switch (status) {
      case "completed":
        return "bg-green-500";
      case "pending":
        return "bg-yellow-500";
      case "cancelled":
        return "bg-red-500";
      default:
        return "bg-blue-500";
    }
  };

  // Handle refresh
  const handleRefresh = useCallback(async () => {
    try {
      await queryClient.invalidateQueries({ queryKey: ["/api/orders"] });
      toast({
        title: "Orders Updated",
        description: "Latest order information loaded",
      });
    } catch (error) {
      toast({
        title: "Refresh Failed",
        description: "Could not refresh order information",
        variant: "destructive",
      });
    }
  }, [queryClient, toast]);

  return (
    <div className="min-h-screen flex flex-col bg-secondary">
      <AppHeader />

      <div className="flex-1 overflow-y-auto">
        <main className="p-5">
          <div className="flex justify-between items-center mb-4">
            <h1 className="font-semibold text-2xl text-primary">Order History</h1>
            <Button 
              variant="outline" 
              size="sm" 
              onClick={handleRefresh}
              disabled={isRefetching}
            >
              {isRefetching ? (
                <Loader2 className="h-4 w-4 mr-2 animate-spin" />
              ) : (
                <RefreshCw className="h-4 w-4 mr-2" />
              )}
              Refresh
            </Button>
          </div>

          {isLoading ? (
            <div className="flex justify-center items-center h-64">
              <Loader2 className="h-8 w-8 animate-spin text-primary" />
            </div>
          ) : error ? (
            <Card>
              <CardContent className="p-6 text-center">
                <p className="text-red-500">Failed to load orders</p>
              </CardContent>
            </Card>
          ) : sortedOrders.length === 0 ? (
            <Card>
              <CardContent className="p-6 text-center">
                <p className="text-gray-500">You haven't placed any orders yet</p>
              </CardContent>
            </Card>
          ) : (
            <div className="grid gap-4">
              {sortedOrders.map((order) => (
                <Card key={order.id}>
                  <CardHeader className="pb-3">
                    <div className="flex justify-between items-center">
                      <CardTitle className="text-lg">Order #{order.id}</CardTitle>
                      <Badge className={getStatusColor(order.status)}>
                        {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                      </Badge>
                    </div>
                    <CardDescription>
                      Placed on {formatDate(new Date(order.createdAt))}
                    </CardDescription>
                  </CardHeader>
                  <CardContent>
                    <Accordion type="single" collapsible className="w-full">
                      <AccordionItem value="items">
                        <AccordionTrigger>Order Details</AccordionTrigger>
                        <AccordionContent>
                          <div className="space-y-2">
                            {(order.items as CartItem[]).map((item: CartItem, idx: number) => (
                              <div key={idx} className="flex justify-between py-2">
                                <div>
                                  <div className="font-medium">{item.name}</div>
                                  <div className="text-sm text-gray-500">
                                    Quantity: {item.quantity}
                                  </div>
                                </div>
                                <div className="font-medium">
                                  ${(item.price * item.quantity).toFixed(2)}
                                </div>
                              </div>
                            ))}
                            <Separator className="my-2" />
                            <div className="flex justify-between font-semibold">
                              <div>Total</div>
                              <div>{formatCurrency(order.total)}</div>
                            </div>
                          </div>
                        </AccordionContent>
                      </AccordionItem>
                    </Accordion>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

client/src/pages/profile-page.tsx.content:
import { AppHeader } from "@/components/app-header";

import { QRCode } from "@/components/qr-code";
import { PushNotificationToggle } from "@/components/push-notification-toggle";
import { BuyCredits } from "@/components/buy-credits";
import { SendCredits } from "@/components/send-credits";
import { TransactionHistory } from "@/components/transaction-history";
import AppInstallButton from "@/components/app-install-button";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Separator } from "@/components/ui/separator";
import { Switch } from "@/components/ui/switch";
import { useAuth } from "@/hooks/use-auth";
import { useBiometricAuth } from "@/hooks/use-biometric-auth";
import { useForm } from "react-hook-form";
import { z } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "../lib/queryClient";
import { useEffect } from "react";
import { useAppUpdate } from "@/contexts/app-update-context";

const profileFormSchema = z.object({
  username: z.string(),
  email: z.string().email().optional().or(z.literal("")),
  fullName: z.string().optional().or(z.literal("")),
  phoneNumber: z.string().optional().or(z.literal("")),
  notifications: z.boolean().default(true),
  marketing: z.boolean().default(false),
});

type ProfileFormValues = z.infer<typeof profileFormSchema>;

// Add testNotification to Window interface
declare global {
  interface Window {
    testNotification?: () => void;
  }
}

export default function ProfilePage() {
  const { user } = useAuth();
  const { toast } = useToast();
  const { checkForUpdates, updateAvailable, applyUpdate } = useAppUpdate();

  const {
    biometricState,
    setupBiometricAuth,
    disableBiometricAuth,
    getBiometricDisplayName,
  } = useBiometricAuth();

  // Create a single mutation for testing push notifications
  const testNotificationMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest('POST', '/api/push/test');
      return await res.json();
    },
    onSuccess: () => {
      toast({
        title: "Test notification sent",
        description: "If notifications are enabled, you should receive it shortly.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Test notification failed",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  // Handler for sending a test notification
  const handleSendTestNotification = () => {
    // First, send a message to all clients with a potential notification payload
    if ('serviceWorker' in navigator && navigator.serviceWorker.controller) {
      const timestamp = new Date().toISOString();
      const testId = Math.random().toString(36).substring(2, 10);

      // Create notification data to be verified by service worker
      const notificationData = {
        title: "Test Notification",
        body: `This is a test notification (${new Date().toLocaleTimeString()})`,
        timestamp: Date.now(),
        data: {
          testId,
          url: "/profile",
          timestamp,
          orderId: 999, // For test notifications
          status: "test",
          isTestNotification: true,
          userId: user?.id // Include user ID for verification
        }
      };

      console.log('Sending TEST_NOTIFICATION message to service worker with user ID:', user?.id);

      // Send test notification message to service worker
      navigator.serviceWorker.controller.postMessage({
        type: 'TEST_NOTIFICATION',
        notificationData
      });
    }

    // Also trigger the backend mutation to send the test notification
    testNotificationMutation.mutate();
  };

  // Expose the test notification function to the window object
  // so it can be called from the PushNotificationToggle component
  useEffect(() => {
    window.testNotification = handleSendTestNotification;

    // Clean up when component unmounts
    return () => {
      delete window.testNotification;
    };
  }, [handleSendTestNotification, user?.id]);

  const form = useForm<ProfileFormValues>({
    resolver: zodResolver(profileFormSchema),
    defaultValues: {
      username: user?.username || "",
      email: user?.email || "",
      fullName: user?.fullName || "",
      phoneNumber: user?.phoneNumber || "",
      notifications: true,
      marketing: false,
    },
  });

  // Create a mutation for updating the profile
  const updateProfileMutation = useMutation({
    mutationFn: async (data: Partial<ProfileFormValues>) => {
      const response = await apiRequest("PATCH", "/api/user/profile", data);
      return response.json();
    },
    onSuccess: (updatedUser) => {
      // Update the user data in the cache
      queryClient.setQueryData(["/api/user"], updatedUser);

      toast({
        title: "Profile updated",
        description: "Your profile has been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Update failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  async function onSubmit(data: ProfileFormValues) {
    // Extract only the profile fields we want to update (not username, which is read-only)
    const { username, notifications, marketing, ...updateData } = data;

    try {
      await updateProfileMutation.mutateAsync(updateData);
    } catch (error) {
      // Error is handled by the mutation's onError callback
      console.error("Error updating profile:", error);
    }
  }

  return (
    <div className="min-h-screen flex flex-col bg-secondary">
      <AppHeader />

      <main className="flex-1 p-5">
        <div className="grid gap-6">
          <div className="flex items-center justify-between">
            <h1 className="font-semibold text-2xl text-primary">Profile Settings</h1>
          </div>

          <Card>
            <CardHeader>
              <CardTitle>Account Information</CardTitle>
              <CardDescription>
                Manage your account details and preferences
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                  <div className="grid gap-4">
                    <FormField
                      control={form.control}
                      name="username"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Username</FormLabel>
                          <FormControl>
                            <Input {...field} disabled />
                          </FormControl>
                          <FormDescription>
                            Your username cannot be changed
                          </FormDescription>
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="fullName"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Full Name</FormLabel>
                          <FormControl>
                            <Input {...field} placeholder="Enter your full name" />
                          </FormControl>
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="email"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Email</FormLabel>
                          <FormControl>
                            <Input {...field} placeholder="Enter your email" type="email" />
                          </FormControl>
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="phoneNumber"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Phone Number</FormLabel>
                          <FormControl>
                            <Input {...field} placeholder="Enter your phone number" />
                          </FormControl>
                        </FormItem>
                      )}
                    />
                  </div>

                  <Separator />

                  <div className="grid gap-4">
                    <h3 className="text-lg font-medium">Notifications</h3>

                    <FormField
                      control={form.control}
                      name="notifications"
                      render={({ field }) => (
                        <FormItem className="flex flex-row items-center justify-between">
                          <div className="space-y-0.5">
                            <FormLabel>Order Notifications</FormLabel>
                            <FormDescription>
                              Receive notifications about your orders
                            </FormDescription>
                          </div>
                          <FormControl>
                            <Switch
                              checked={field.value}
                              onCheckedChange={field.onChange}
                            />
                          </FormControl>
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="marketing"
                      render={({ field }) => (
                        <FormItem className="flex flex-row items-center justify-between">
                          <div className="space-y-0.5">
                            <FormLabel>Marketing Emails</FormLabel>
                            <FormDescription>
                              Receive emails about promotions and special offers
                            </FormDescription>
                          </div>
                          <FormControl>
                            <Switch
                              checked={field.value}
                              onCheckedChange={field.onChange}
                            />
                          </FormControl>
                        </FormItem>
                      )}
                    />
                  </div>

                  <Button 
                    type="submit" 
                    className="w-full"
                    disabled={updateProfileMutation.isPending}
                  >
                    {updateProfileMutation.isPending ? 'Updating...' : 'Update Profile'}
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>

          {/* Biometric Authentication Settings */}
          <Card>
            <CardHeader>
              <CardTitle>Biometric Authentication</CardTitle>
              <CardDescription>
                Secure and convenient login using Face ID, Touch ID, or fingerprint
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {biometricState.isLoading ? (
                <div className="flex items-center space-x-2">
                  <div className="w-4 h-4 border-2 border-gray-300 border-t-green-600 rounded-full animate-spin" />
                  <span className="text-sm text-muted-foreground">Checking biometric availability...</span>
                </div>
              ) : biometricState.isAvailable ? (
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <div className="space-y-1">
                      <div className="flex items-center space-x-2">
                        <span className="font-medium">{getBiometricDisplayName(biometricState.biometricType)}</span>
                        {biometricState.hasStoredCredentials && (
                          <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                            Enabled
                          </span>
                        )}
                      </div>
                      <p className="text-sm text-muted-foreground">
                        {biometricState.hasStoredCredentials 
                          ? `You can sign in quickly using ${getBiometricDisplayName(biometricState.biometricType)}`
                          : `Enable ${getBiometricDisplayName(biometricState.biometricType)} for faster sign-in`
                        }
                      </p>
                    </div>
                    <Switch
                      checked={biometricState.hasStoredCredentials}
                      onCheckedChange={async (enabled) => {
                        if (enabled) {
                          // Would prompt user to sign in again to save credentials
                          toast({
                            title: "Setup Required",
                            description: "Sign out and back in to enable biometric authentication",
                          });
                        } else {
                          await disableBiometricAuth();
                        }
                      }}
                    />
                  </div>

                  {biometricState.hasStoredCredentials && (
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={disableBiometricAuth}
                      className="w-full"
                    >
                      Disable {getBiometricDisplayName(biometricState.biometricType)}
                    </Button>
                  )}
                </div>
              ) : (
                <div className="text-center py-4">
                  <div className="w-12 h-12 mx-auto mb-3 bg-gray-100 rounded-full flex items-center justify-center">
                    <span className="text-gray-400">üîí</span>
                  </div>
                  <p className="text-sm text-muted-foreground">
                    Biometric authentication is not available on this device or browser. 
                    This feature works on mobile devices with Face ID, Touch ID, or fingerprint sensors.
                  </p>
                </div>
              )}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Account Balance</CardTitle>
              <CardDescription>
                Your current credit balance is <span className="font-medium">${user?.credits.toFixed(2)}</span>
              </CardDescription>
            </CardHeader>
            <CardContent className="pt-0">
              <div className="space-y-3">
                <BuyCredits />
                <SendCredits />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Your QR Code</CardTitle>
              <CardDescription>
                Scan this code to identify yourself at the coffee shop or receive credits from other users
              </CardDescription>
            </CardHeader>
            <CardContent className="flex justify-center">
              <div className="w-48 h-48">
                <QRCode />
              </div>
            </CardContent>
          </Card>

          <TransactionHistory />

          <AppInstallButton />

          <PushNotificationToggle />

          <Card>
            <CardHeader>
              <CardTitle>Debug Tools</CardTitle>
              <CardDescription>Utilities for debugging the application</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid gap-2">
                <div className="text-sm font-semibold">Notification Status</div>
                <ul className="list-disc pl-5 text-sm">
                  <li>Push API Supported: {'Notification' in window ? 'Yes' : 'No'}</li>
                  <li>Service Worker Supported: {'serviceWorker' in navigator ? 'Yes' : 'No'}</li>
                  <li>Notification Permission: {typeof window !== 'undefined' && 'Notification' in window ? window.Notification.permission : 'unknown'}</li>
                  <li>Is Windows: {typeof window !== 'undefined' ? window.navigator.userAgent.includes('Windows') ? 'Yes' : 'No' : 'unknown'}</li>
                </ul>
              </div>

              <div className="flex flex-col gap-3 pt-2">
                <Button 
                  variant="outline"
                  onClick={() => {
                    if ('serviceWorker' in navigator) {
                      navigator.serviceWorker.ready
                        .then(reg => {
                          const state = reg.active?.state;
                          console.log('Service worker state:', state);
                          toast({
                            title: 'Service Worker Status',
                            description: `State: ${state || 'unknown'}`,
                            duration: 3000
                          });
                        })
                        .catch(err => {
                          console.error('Service worker error:', err);
                          toast({
                            title: 'Service Worker Error',
                            description: String(err),
                            variant: 'destructive',
                            duration: 5000
                          });
                        });
                    } else {
                      toast({
                        title: 'Service Worker Not Supported',
                        description: 'This browser does not support Service Workers',
                        variant: 'destructive',
                        duration: 5000
                      });
                    }
                  }}
                >
                  Check Service Worker State
                </Button>

                <Button
                  onClick={() => {
                    toast({
                      title: "Sending test notification",
                      description: "Check your notifications and console logs",
                      duration: 3000
                    });
                    handleSendTestNotification();
                  }}
                >
                  Send Test Notification
                </Button>

                <Button
                  variant="outline"
                  onClick={async () => {
                    toast({
                      title: "Checking for updates",
                      description: "Looking for new versions of the app...",
                      duration: 3000
                    });

                    const hasUpdate = await checkForUpdates();

                    if (hasUpdate) {
                      toast({
                        title: "Update Available",
                        description: "A new version of the app is available.",
                        action: (
                          <Button 
                            variant="default" 
                            size="sm" 
                            onClick={applyUpdate}
                          >
                            Update Now
                          </Button>
                        ),
                        duration: 10000
                      });
                    } else {
                      toast({
                        title: "No Updates",
                        description: "You're already using the latest version.",
                        duration: 3000
                      });
                    }
                  }}
                >
                  Check for Updates
                </Button>

                <Button 
                  variant="secondary"
                  onClick={() => {
                    // Force unregister and reregister service worker
                    if ('serviceWorker' in navigator) {
                      navigator.serviceWorker.getRegistrations().then(registrations => {
                        for(let registration of registrations) {
                          console.log('Unregistering service worker:', registration);
                          registration.unregister();
                        }

                        toast({
                          title: 'Service Worker Reset',
                          description: 'Service worker has been unregistered. Refresh the page to reinstall.',
                          duration: 5000
                        });
                      });
                    } else {
                      toast({
                        title: 'Service Worker Not Supported',
                        description: 'This browser does not support Service Workers',
                        variant: 'destructive',
                        duration: 5000
                      });
                    }
                  }}
                >
                  Reset Service Worker
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      </main>


    </div>
  );
}

client/src/pages/send-credits-page.tsx.content:
import { useState } from "react";
import { motion } from "framer-motion";
import { ArrowLeft, Send, MessageSquare, User, DollarSign, Shield, Clock, CheckCircle } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { Link } from "wouter";
import { useMutation, useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { formatDistanceToNow } from "date-fns";

interface ShareCreditsResponse {
  success: boolean;
  verificationCode: string;
  smsMessage: string;
  expiresAt: string;
}

export default function SendCreditsPage() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [phoneNumber, setPhoneNumber] = useState("");
  const [amount, setAmount] = useState("");
  const [showSMSPreview, setShowSMSPreview] = useState(false);
  const [smsDetails, setSmsDetails] = useState<ShareCreditsResponse | null>(null);

  // Format phone number as user types
  const formatPhoneNumber = (value: string) => {
    const numbers = value.replace(/\D/g, '');
    if (numbers.length <= 3) return numbers;
    if (numbers.length <= 6) return `${numbers.slice(0, 3)} ${numbers.slice(3)}`;
    return `${numbers.slice(0, 3)} ${numbers.slice(3, 6)} ${numbers.slice(6, 10)}`;
  };

  const shareCreditsMutation = useMutation({
    mutationFn: async (data: { phoneNumber: string; amount: number }) => {
      const response = await apiRequest("POST", "/api/share-credits", data);
      return await response.json();
    },
    onSuccess: (data: ShareCreditsResponse) => {
      setSmsDetails(data);
      setShowSMSPreview(true);
      toast({
        title: "Credit Share Ready",
        description: "Verification code generated. Send the SMS to complete sharing.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Share Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSendSMS = () => {
    if (!smsDetails) return;

    // Create SMS URL for native SMS app
    const cleanPhone = phoneNumber.replace(/\s/g, '');
    const smsUrl = `sms:${cleanPhone}?body=${encodeURIComponent(smsDetails.smsMessage)}`;

    // Open SMS app
    window.location.href = smsUrl;

    toast({
      title: "SMS App Opened",
      description: "Complete the transfer by sending the text message.",
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const creditAmount = parseFloat(amount);
    if (!phoneNumber || !creditAmount) {
      toast({
        title: "Missing Information",
        description: "Please enter phone number and amount.",
        variant: "destructive",
      });
      return;
    }

    if (creditAmount <= 0) {
      toast({
        title: "Invalid Amount",
        description: "Amount must be greater than $0.",
        variant: "destructive",
      });
      return;
    }

    if (!user || creditAmount > user.credits) {
      toast({
        title: "Insufficient Credits",
        description: "You don't have enough credits for this transfer.",
        variant: "destructive",
      });
      return;
    }

    shareCreditsMutation.mutate({
      phoneNumber: phoneNumber.replace(/\s/g, ''),
      amount: creditAmount
    });
  };

  if (showSMSPreview && smsDetails) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-4">
        <div className="max-w-md mx-auto space-y-6">
          {/* Header */}
          <motion.div 
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            className="flex items-center space-x-4 pt-4"
          >
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setShowSMSPreview(false)}
              className="p-2 h-auto"
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
            <h1 className="text-2xl font-bold text-slate-800">Send SMS</h1>
          </motion.div>

          {/* SMS Preview Card */}
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ delay: 0.1 }}
          >
            <Card className="border-green-200 bg-green-50">
              <CardHeader className="text-center pb-4">
                <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <MessageSquare className="h-8 w-8 text-green-600" />
                </div>
                <CardTitle className="text-green-800">Ready to Send</CardTitle>
                <CardDescription className="text-green-600">
                  Your SMS message is ready. Tap "Send SMS" to open your messaging app.
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                {/* Transfer Details */}
                <div className="bg-white rounded-lg p-4 space-y-3">
                  <div className="flex justify-between">
                    <span className="text-slate-600">To:</span>
                    <span className="font-semibold">{phoneNumber}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-slate-600">Amount:</span>
                    <span className="font-semibold text-green-600">${amount}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-slate-600">Code:</span>
                    <span className="font-mono font-bold text-lg">{smsDetails.verificationCode}</span>
                  </div>
                </div>

                {/* SMS Preview */}
                <div className="bg-slate-100 rounded-lg p-4">
                  <p className="text-sm text-slate-600 mb-2">Message Preview:</p>
                  <div className="bg-white rounded-lg p-3 border-l-4 border-green-500">
                    <p className="text-sm text-slate-800 leading-relaxed">
                      {smsDetails.smsMessage}
                    </p>
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="space-y-3 pt-2">
                  <Button 
                    onClick={handleSendSMS}
                    className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-3"
                  >
                    <Send className="h-5 w-5 mr-2" />
                    Send SMS
                  </Button>

                  <Button 
                    variant="outline" 
                    onClick={() => setShowSMSPreview(false)}
                    className="w-full"
                  >
                    Edit Share
                  </Button>
                </div>

                {/* Important Note */}
                <div className="bg-amber-50 border border-amber-200 rounded-lg p-3">
                  <div className="flex items-start space-x-2">
                    <Shield className="h-4 w-4 text-amber-600 mt-0.5" />
                    <div>
                      <p className="text-xs text-amber-800 font-medium">Important:</p>
                      <p className="text-xs text-amber-700">
                        Credits will be deducted from your account only after staff verify this code at the store counter.
                      </p>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </motion.div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-4">
      <div className="max-w-md mx-auto space-y-6">
        {/* Header */}
        <motion.div 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="flex items-center space-x-4 pt-4"
        >
          <Link href="/">
            <Button variant="ghost" size="sm" className="p-2 h-auto">
              <ArrowLeft className="h-5 w-5" />
            </Button>
          </Link>
          <h1 className="text-2xl font-bold text-slate-800">Share Credits</h1>
        </motion.div>

        {/* Available Balance */}
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.1 }}
        >
          <Card className="bg-gradient-to-r from-green-600 to-green-700 text-white border-0">
            <CardContent className="p-6">
              <div className="flex items-center space-x-3">
                <div className="w-12 h-12 bg-white/20 rounded-full flex items-center justify-center">
                  <DollarSign className="h-6 w-6" />
                </div>
                <div>
                  <p className="text-green-100 text-sm">Available Balance</p>
                  <p className="text-2xl font-bold">${user?.credits?.toFixed(2) || '0.00'}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </motion.div>

        {/* Send Credits Form */}
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.2 }}
        >
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <User className="h-5 w-5 text-green-600" />
                <span>Share Credits</span>
              </CardTitle>
              <CardDescription>
                Share credits with non-members via SMS. They can claim them at our store counter.
              </CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="phone">Recipient's Phone Number</Label>
                  <Input
                    id="phone"
                    type="tel"
                    placeholder="000 000 0000"
                    value={phoneNumber}
                    onChange={(e) => setPhoneNumber(formatPhoneNumber(e.target.value))}
                    maxLength={12}
                    className="text-lg"
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="amount">Amount to Send ($)</Label>
                  <Input
                    id="amount"
                    type="number"
                    placeholder="0.00"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                    step="0.01"
                    min="0.01"
                    max={user?.credits || 0}
                    className="text-lg"
                  />
                  {amount && parseFloat(amount) > 0 && (
                    <p className="text-sm text-slate-600">
                      Remaining balance: ${((user?.credits || 0) - parseFloat(amount)).toFixed(2)}
                    </p>
                  )}
                </div>

                <Button
                  type="submit"
                  className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-3"
                  disabled={shareCreditsMutation.isPending || !phoneNumber || !amount}
                >
                  {shareCreditsMutation.isPending ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2" />
                      Generating Code...
                    </>
                  ) : (
                    <>
                      <Send className="h-5 w-5 mr-2" />
                      Generate SMS Code
                    </>
                  )}
                </Button>
              </form>
            </CardContent>
          </Card>
        </motion.div>

        {/* How It Works */}
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.3 }}
        >
          <Card className="bg-slate-100 border-slate-200">
            <CardContent className="p-4">
              <h3 className="font-semibold text-slate-800 mb-3">How It Works</h3>
              <div className="space-y-2 text-sm text-slate-600">
                <div className="flex items-start space-x-2">
                  <span className="w-5 h-5 bg-green-100 text-green-600 rounded-full flex items-center justify-center text-xs font-semibold mt-0.5">1</span>
                  <p>Enter non-member's phone number and credit amount</p>
                </div>
                <div className="flex items-start space-x-2">
                  <span className="w-5 h-5 bg-green-100 text-green-600 rounded-full flex items-center justify-center text-xs font-semibold mt-0.5">2</span>
                  <p>We generate a unique verification code</p>
                </div>
                <div className="flex items-start space-x-2">
                  <span className="w-5 h-5 bg-green-100 text-green-600 rounded-full flex items-center justify-center text-xs font-semibold mt-0.5">3</span>
                  <p>Send SMS using your phone's messaging app</p>
                </div>
                <div className="flex items-start space-x-2">
                  <span className="w-5 h-5 bg-green-100 text-green-600 rounded-full flex items-center justify-center text-xs font-semibold mt-0.5">4</span>
                  <p>Non-member shows code at store counter</p>
                </div>
                <div className="flex items-start space-x-2">
                  <span className="w-5 h-5 bg-green-100 text-green-600 rounded-full flex items-center justify-center text-xs font-semibold mt-0.5">5</span>
                  <p>Staff verifies code and deducts credits from your account</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </motion.div>
      </div>
    </div>
  );
}

