import { NativeBiometric } from 'capacitor-native-biometric';

export interface BiometricCredentials {
  username: string;
  password: string;
}

class BiometricService {
  private readonly CREDENTIAL_KEY = 'bean-stalker-credentials';

  /**
   * Check if biometric authentication is available on the device
   */
  async isAvailable(): Promise<boolean> {
    try {
      const result = await NativeBiometric.isAvailable();
      return result.isAvailable;
    } catch (error) {
      console.log('Biometric authentication not available:', error);
      return false;
    }
  }

  /**
   * Get available biometric types (Face ID, Touch ID, Fingerprint)
   */
  async getBiometricType(): Promise<string> {
    try {
      const result = await NativeBiometric.isAvailable();
      return result.biometryType || 'unknown';
    } catch (error) {
      console.log('Could not determine biometric type:', error);
      return 'unknown';
    }
  }

  /**
   * Save user credentials securely for biometric authentication
   */
  async saveCredentials(username: string, password: string): Promise<boolean> {
    try {
      await NativeBiometric.setCredentials({
        username,
        password,
        server: this.CREDENTIAL_KEY,
      });
      return true;
    } catch (error) {
      console.error('Failed to save biometric credentials:', error);
      return false;
    }
  }

  /**
   * Authenticate user with biometrics and retrieve credentials
   */
  async authenticateWithBiometrics(): Promise<BiometricCredentials | null> {
    try {
      // Check if biometrics are available
      const isAvailable = await this.isAvailable();
      if (!isAvailable) {
        throw new Error('Biometric authentication not available');
      }

      // Get biometric type for customized messaging
      const biometricType = await this.getBiometricType();
      const reason = this.getAuthenticationReason(biometricType);

      // Perform biometric authentication
      const result = await NativeBiometric.verifyIdentity({
        reason,
        title: 'Bean Stalker Authentication',
        subtitle: 'Access your coffee account securely',
        description: 'Use your biometric authentication to sign in'
      });

      if (result) {
        // Retrieve stored credentials
        const credentials = await NativeBiometric.getCredentials({
          server: this.CREDENTIAL_KEY,
        });

        return {
          username: credentials.username,
          password: credentials.password,
        };
      }

      return null;
    } catch (error) {
      console.error('Biometric authentication failed:', error);
      throw error;
    }
  }

  /**
   * Delete stored biometric credentials
   */
  async deleteCredentials(): Promise<boolean> {
    try {
      await NativeBiometric.deleteCredentials({
        server: this.CREDENTIAL_KEY,
      });
      return true;
    } catch (error) {
      console.error('Failed to delete biometric credentials:', error);
      return false;
    }
  }

  /**
   * Get user-friendly authentication reason based on biometric type
   */
  private getAuthenticationReason(biometricType: string): string {
    if (!biometricType || typeof biometricType !== 'string') {
      return 'Use biometric authentication to access Bean Stalker';
    }

    switch (biometricType.toLowerCase()) {
      case 'faceid':
        return 'Use Face ID to access Bean Stalker';
      case 'touchid':
        return 'Use Touch ID to access Bean Stalker';
      case 'fingerprint':
        return 'Use your fingerprint to access Bean Stalker';
      default:
        return 'Use biometric authentication to access Bean Stalker';
    }
  }

  /**
   * Check if user has biometric credentials saved
   */
  async hasStoredCredentials(): Promise<boolean> {
    try {
      const credentials = await NativeBiometric.getCredentials({
        server: this.CREDENTIAL_KEY,
      });
      return !!(credentials.username && credentials.password);
    } catch (error) {
      return false;
    }
  }
}

export const biometricService = new BiometricService();

client/src/services/iap-service.ts.content:
import { Purchases, PurchasesOffering, PurchasesPackage, LOG_LEVEL } from '@revenuecat/purchases-capacitor';
import { Capacitor } from '@capacitor/core';

export interface IAPProduct {
  id: string;
  title: string;
  description: string;
  price: string;
  priceAmountMicros: number;
  priceCurrencyCode: string;
  type: 'membership' | 'credits';
}

export interface PurchaseResult {
  success: boolean;
  productId: string;
  transactionId?: string;
  receipt?: string;
  purchaseData?: any;
  error?: string;
}

class IAPService {
  private isInitialized = false;
  private offerings: PurchasesOffering[] = [];

  // Product IDs - matching your App Store Connect products
  private readonly PRODUCT_IDS = {
    PREMIUM_MEMBERSHIP: 'com.beanstalker.membership69',
    CREDITS_25: 'com.beanstalker.credit25', 
    CREDITS_50: 'com.beanstalker.credit50',
    CREDITS_100: 'com.beanstalker.credit100'
  };

  async initialize(): Promise<boolean> {
    // Only use development mode when not on native platform
    // Always use RevenueCat on iOS/Android even during development
    const isDevelopmentMode = !Capacitor.isNativePlatform();

    if (isDevelopmentMode) {
      console.log('IAP: Running in web development mode - simulating IAP functionality');
      this.isInitialized = true;
      return true;
    }

    // Native platform - always use RevenueCat
    if (!import.meta.env.VITE_REVENUECAT_API_KEY) {
      console.error('IAP: VITE_REVENUECAT_API_KEY is required for native platforms');
      return false;
    }

    try {
      // Configure RevenueCat for production with enhanced logging
      await Purchases.setLogLevel({ level: LOG_LEVEL.DEBUG });

      const apiKey = import.meta.env.VITE_REVENUECAT_API_KEY;
      console.log('IAP: Configuring RevenueCat with API key present:', !!apiKey);

      await Purchases.configure({
        apiKey,
        appUserID: undefined, // Will be set when user logs in
      });
      console.log('IAP: RevenueCat configured successfully');

      // Check if In-App Purchases are available on device
      const canMakePayments = await Purchases.canMakePayments();
      console.log('IAP: Device can make payments:', canMakePayments);

      if (!canMakePayments) {
        console.error('IAP: Device cannot make payments - IAP restricted or not available');
        return false;
      }

      // Get available offerings
      await this.loadOfferings();

      this.isInitialized = true;
      console.log('IAP: Service initialized successfully with', this.offerings.length, 'offerings');
      return true;
    } catch (error) {
      console.error('IAP: Failed to initialize', error);
      console.error('IAP: Error details:', JSON.stringify(error, null, 2));
      return false;
    }
  }

  async setUserID(userID: string): Promise<void> {
    if (!this.isInitialized) {
      console.error('IAP: Cannot set user ID - service not initialized');
      return;
    }

    try {
      console.log('IAP: Logging in user with ID:', userID);
      const result = await Purchases.logIn({ appUserID: userID });
      console.log('IAP: User login successful:', result.customerInfo.originalAppUserId);
      console.log('IAP: Customer info:', JSON.stringify(result.customerInfo, null, 2));
    } catch (error) {
      console.error('IAP: Failed to set user ID', error);
      console.error('IAP: User login error details:', JSON.stringify(error, null, 2));
    }
  }

  private async loadOfferings(): Promise<void> {
    try {
      console.log('IAP: Loading offerings from RevenueCat...');
      const offerings = await Purchases.getOfferings();

      console.log('IAP: Raw offerings response:', JSON.stringify(offerings, null, 2));

      if (offerings.current) {
        this.offerings = [offerings.current];
        console.log('IAP: Using current offering:', offerings.current.identifier);
      } else {
        this.offerings = Object.values(offerings.all);
        console.log('IAP: Using all offerings:', Object.keys(offerings.all));
      }

      console.log('IAP: Loaded', this.offerings.length, 'offerings');

      // Log available packages in each offering
      this.offerings.forEach((offering, index) => {
        console.log(`IAP: Offering ${index + 1}: ${offering.identifier}`);
        offering.availablePackages.forEach((pkg, pkgIndex) => {
          console.log(`  Package ${pkgIndex + 1}: ${pkg.product.identifier} - ${pkg.product.title} - ${pkg.product.priceString}`);
        });
      });

      if (this.offerings.length === 0) {
        console.warn('IAP: No offerings available - check App Store Connect product configuration');
      }
    } catch (error) {
      console.error('IAP: Failed to load offerings', error);
      console.error('IAP: Offerings error details:', JSON.stringify(error, null, 2));
    }
  }

  async getAvailableProducts(): Promise<IAPProduct[]> {
    if (!this.isInitialized) {
      throw new Error('IAP service not initialized');
    }

    // Development mode - return mock products (web only)
    const isDevelopmentMode = !Capacitor.isNativePlatform();

    if (isDevelopmentMode) {
      return [
        {
          id: this.PRODUCT_IDS.PREMIUM_MEMBERSHIP,
          title: 'Premium Membership',
          description: 'Full access to Bean Stalker premium features',
          price: 'AUD $69.00',
          priceAmountMicros: 69000000,
          priceCurrencyCode: 'AUD',
          type: 'membership'
        },

        {
          id: this.PRODUCT_IDS.CREDITS_25,
          title: '$29.50 Credits',
          description: 'Get $29.50 credits for $25 - $4.50 bonus!',
          price: 'AUD $25.00',
          priceAmountMicros: 25000000,
          priceCurrencyCode: 'AUD',
          type: 'credits'
        },
        {
          id: this.PRODUCT_IDS.CREDITS_50,
          title: '$59.90 Credits',
          description: 'Get $59.90 credits for $50 - $9.90 bonus!',
          price: 'AUD $50.00',
          priceAmountMicros: 50000000,
          priceCurrencyCode: 'AUD',
          type: 'credits'
        },
        {
          id: this.PRODUCT_IDS.CREDITS_100,
          title: '$120.70 Credits',
          description: 'Get $120.70 credits for $100 - $20.70 bonus!',
          price: 'AUD $100.00',
          priceAmountMicros: 100000000,
          priceCurrencyCode: 'AUD',
          type: 'credits'
        }
      ];
    }

    // Production mode - use RevenueCat
    const products: IAPProduct[] = [];

    for (const offering of this.offerings) {
      for (const packageObj of offering.availablePackages) {
        const product = packageObj.product;

        products.push({
          id: product.identifier,
          title: product.title,
          description: product.description,
          price: product.priceString,
          priceAmountMicros: product.price * 1000000, // Convert to micros
          priceCurrencyCode: product.currencyCode,
          type: this.getProductType(product.identifier)
        });
      }
    }

    return products;
  }

  private getProductType(productId: string): 'membership' | 'credits' {
    if (productId.includes('membership')) {
      return 'membership';
    }
    return 'credits';
  }

  async purchaseProduct(productId: string): Promise<PurchaseResult> {
    if (!this.isInitialized) {
      throw new Error('IAP service not initialized');
    }

    // Development mode - simulate successful purchase (web only)
    const isDevelopmentMode = !Capacitor.isNativePlatform();

    if (isDevelopmentMode) {
      console.log(`IAP: Simulating purchase for ${productId}`);

      // Simulate processing delay
      await new Promise(resolve => setTimeout(resolve, 1500));

      return {
        success: true,
        productId,
        transactionId: `dev_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        receipt: JSON.stringify({
          productId,
          purchaseDate: new Date().toISOString(),
          environment: 'development'
        })
      };
    }

    // Production mode - use RevenueCat
    try {
      // Find the package for this product
      let targetPackage: PurchasesPackage | null = null;

      for (const offering of this.offerings) {
        for (const packageObj of offering.availablePackages) {
          if (packageObj.product.identifier === productId) {
            targetPackage = packageObj;
            break;
          }
        }
        if (targetPackage) break;
      }

      if (!targetPackage) {
        throw new Error(`Product ${productId} not found`);
      }

      // Make the purchase
      const result = await Purchases.purchasePackage({ 
        aPackage: targetPackage 
      });

      return {
        success: true,
        productId,
        transactionId: result.customerInfo.originalAppUserId,
        receipt: JSON.stringify(result.customerInfo)
      };
    } catch (error: any) {
      console.error('IAP: Purchase failed', error);

      // Handle user cancellation
      if (error.code === 'PURCHASE_CANCELLED') {
        return {
          success: false,
          productId,
          error: 'Purchase cancelled by user'
        };
      }

      return {
        success: false,
        productId,
        error: error.message || 'Purchase failed'
      };
    }
  }

  async restorePurchases(): Promise<boolean> {
    if (!this.isInitialized) {
      throw new Error('IAP service not initialized');
    }

    try {
      const result = await Purchases.restorePurchases();
      console.log('IAP: Purchases restored', result);
      return true;
    } catch (error) {
      console.error('IAP: Failed to restore purchases', error);
      return false;
    }
  }

  isAvailable(): boolean {
    // Available when properly initialized (web dev mode or native platform)
    const isDevelopmentMode = !Capacitor.isNativePlatform();
    return this.isInitialized && (isDevelopmentMode || Capacitor.isNativePlatform());
  }

  // Convert credit amount to product ID
  getCreditsProductId(amount: number): string {
    switch (amount) {
      case 25: return this.PRODUCT_IDS.CREDITS_25;
      case 50: return this.PRODUCT_IDS.CREDITS_50;
      case 100: return this.PRODUCT_IDS.CREDITS_100;
      default: return this.PRODUCT_IDS.CREDITS_25;
    }
  }

  getMembershipProductId(): string {
    return this.PRODUCT_IDS.PREMIUM_MEMBERSHIP;
  }
}

export const iapService = new IAPService();

client/src/utils/qr-scanner.ts.content:
/**
 * A simple utility for QR code scanning
 * Requires jsQR library to be available
 */

interface Point {
  x: number;
  y: number;
}

export interface QRCodeDetectOptions {
  inversionAttempts?: 'dontInvert' | 'onlyInvert' | 'attemptBoth';
}

export interface QRCodeResult {
  binaryData: Uint8Array;
  data: string;
  chunks: any[];
  version: number;
  location: {
    topRightCorner: Point;
    topLeftCorner: Point;
    bottomRightCorner: Point;
    bottomLeftCorner: Point;
  };
}

export class QRScanner {
  private video: HTMLVideoElement | null = null;
  private canvas: HTMLCanvasElement;
  private context: CanvasRenderingContext2D;
  private scanInterval: number | null = null;
  private onDetected: (result: QRCodeResult) => void;
  private jsQRPromise: Promise<any> | null = null;
  private isActive: boolean = false;
  private startTime: number = 0;

  constructor(onDetected: (result: QRCodeResult) => void) {
    this.canvas = document.createElement('canvas');
    const context = this.canvas.getContext('2d');
    if (!context) {
      throw new Error('Failed to get canvas context');
    }
    this.context = context;
    this.onDetected = onDetected;

    // Dynamically import jsQR
    this.jsQRPromise = this.loadJsQR();

    // Log constructor completion
    console.log("QRScanner instance created");
  }

  private async loadJsQR() {
    try {
      console.log('Loading jsQR library...');

      // Check if jsQR is already available
      if (typeof (window as any).jsQR === 'function') {
        console.log('jsQR is already available');
        return (window as any).jsQR;
      }

      // We'll add the script tag to load jsQR from CDN
      return new Promise((resolve, reject) => {
        const script = document.createElement('script');

        // Try using unpkg as primary source
        script.src = 'https://unpkg.com/jsqr@1.4.0/dist/jsQR.js';

        script.onload = () => {
          // Check if window.jsQR is available
          if (typeof (window as any).jsQR === 'function') {
            console.log('jsQR library loaded successfully');
            resolve((window as any).jsQR);
          } else {
            console.error('jsQR library loaded but function not available');
            reject(new Error('jsQR library loaded but function not available'));
          }
        };

        script.onerror = () => {
          console.error('Failed to load jsQR from unpkg, trying jsdelivr...');

          // Try using jsdelivr as fallback
          const fallbackScript = document.createElement('script');
          fallbackScript.src = 'https://cdn.jsdelivr.net/npm/jsqr@1.4.0/dist/jsQR.min.js';

          fallbackScript.onload = () => {
            if (typeof (window as any).jsQR === 'function') {
              console.log('jsQR library loaded successfully from fallback');
              resolve((window as any).jsQR);
            } else {
              console.error('jsQR library loaded from fallback but function not available');
              reject(new Error('jsQR library loaded but function not available'));
            }
          };

          fallbackScript.onerror = () => {
            console.error('Failed to load jsQR from both sources');
            reject(new Error('Failed to load jsQR library from all sources'));
          };

          document.head.appendChild(fallbackScript);
        };

        document.head.appendChild(script);
      });
    } catch (error) {
      console.error('Error loading jsQR:', error);
      throw error;
    }
  }

  /**
   * Start scanning for QR codes from a video element
   */
  public async start(video: HTMLVideoElement) {
    try {
      // If already active, stop first to ensure clean state
      if (this.isActive) {
        console.log('Scanner already active, stopping before restart');
        this.stop();
      }

      if (!video) {
        throw new Error('Video element is null or undefined');
      }

      // Store start time for diagnostic purposes
      this.startTime = Date.now();
      console.log(`Starting QR scanner with video element (id=${video.id}, width=${video.width}, height=${video.height})`);

      // Ensure jsQR is loaded
      await this.jsQRPromise;

      // Set as active early to prevent concurrent start attempts
      this.isActive = true;
      this.video = video;

      // Setup camera stream if not already set
      if (!video.srcObject) {
        console.log('Setting up camera stream...');
        try {
          // Try with ideal settings first
          const constraints = {
            video: { 
              facingMode: 'environment', // Prefer back camera
              width: { ideal: 1280 },
              height: { ideal: 720 }
            }
          };

          try {
            // First try with ideal settings
            console.log('Requesting camera with preferred settings...');
            const stream = await navigator.mediaDevices.getUserMedia(constraints);
            console.log('Camera stream obtained with tracks:', stream.getTracks().length);

            // Verify video element is still valid before proceeding
            if (!this.video || this.video !== video) {
              console.warn('Video element changed during initialization');
              stream.getTracks().forEach(track => track.stop());
              throw new Error('Video element reference changed during initialization');
            }

            video.srcObject = stream;
            console.log('Camera stream attached to video element');
          } catch (preferredError) {
            console.warn('Could not access camera with preferred settings, trying fallback...', preferredError);

            // Fallback to basic constraints
            const fallbackConstraints = {
              video: true,
              audio: false
            };

            try {
              console.log('Requesting camera with fallback settings...');
              const fallbackStream = await navigator.mediaDevices.getUserMedia(fallbackConstraints);
              console.log('Fallback camera stream obtained with tracks:', fallbackStream.getTracks().length);

              // Verify video element is still valid
              if (!this.video || this.video !== video) {
                console.warn('Video element changed during fallback initialization');
                fallbackStream.getTracks().forEach(track => track.stop());
                throw new Error('Video element reference changed during fallback initialization');
              }

              video.srcObject = fallbackStream;
              console.log('Fallback camera stream attached to video element');
            } catch (fallbackError: any) {
              // If still failing, provide detailed error information
              console.error('Camera access with fallback settings failed:', fallbackError);

              // Check if it's a permission error
              if (fallbackError.name === 'NotAllowedError' || fallbackError.name === 'PermissionDeniedError') {
                throw new Error('Camera permission denied. Please allow camera access in your browser settings.');
              } else if (fallbackError.name === 'NotFoundError' || fallbackError.name === 'DevicesNotFoundError') {
                throw new Error('No camera found on this device.');
              } else if (fallbackError.name === 'NotReadableError' || fallbackError.name === 'TrackStartError') {
                throw new Error('Camera is in use by another application or not accessible.');
              } else {
                throw new Error(`Could not access camera: ${fallbackError.message || 'Unknown error'}`);
              }
            }
          }

          // Verify we have a valid srcObject
          if (!video.srcObject) {
            throw new Error('Failed to set video source after obtaining camera stream');
          }

          // Wait for video to be ready
          await new Promise<void>((resolve, reject) => {
            // Verify video element is still valid
            if (!this.video || this.video !== video) {
              reject(new Error('Video element reference changed during metadata loading'));
              return;
            }

            // Add timeout to handle stalled video loading
            const timeout = setTimeout(() => {
              reject(new Error('Video loading timed out. Camera may not be working properly.'));
            }, 5000);

            // Handler for metadata load
            const handleMetadataLoaded = () => {
              clearTimeout(timeout);
              console.log('Video metadata loaded, attempting to play...');

              // Verify video element is still valid before playing
              if (!this.video || this.video !== video) {
                reject(new Error('Video element reference changed before play'));
                return;
              }

              video.play().then(() => {
                console.log('Camera stream started successfully');
                resolve();
              }).catch(err => {
                console.error('Failed to play video:', err);
                reject(new Error(`Video cannot be played: ${err.message}`));
              });
            };

            // Handler for video error
            const handleVideoError = (err: Event) => {
              clearTimeout(timeout);
              console.error('Video element error:', err);
              reject(new Error('Error loading video from camera'));
            };

            // Set up event handlers
            video.addEventListener('loadedmetadata', handleMetadataLoaded, { once: true });
            video.addEventListener('error', handleVideoError, { once: true });

            // Cleanup function to remove event listeners if promise resolves or rejects elsewhere
            const cleanup = () => {
              video.removeEventListener('loadedmetadata', handleMetadataLoaded);
              video.removeEventListener('error', handleVideoError);
            };

            // Ensure cleanup happens when promise resolves or rejects
            setTimeout(() => cleanup, 0);
          });
        } catch (streamError: any) {
          console.error('Error getting camera stream:', streamError);

          // Provide more specific error messages based on the error
          let errorMessage = 'Camera access failed';

          if (streamError.name === 'NotAllowedError' || streamError.name === 'PermissionDeniedError') {
            errorMessage = 'Camera permission denied. Please allow camera access in your browser settings.';
          } else if (streamError.name === 'NotFoundError' || streamError.name === 'DevicesNotFoundError') {
            errorMessage = 'No camera found on this device.';
          } else if (streamError.name === 'NotReadableError' || streamError.name === 'TrackStartError') {
            errorMessage = 'Camera is in use by another application or not accessible.';
          } else if (streamError.message) {
            errorMessage = `Camera access denied or not available: ${streamError.message}`;
          }

          throw new Error(errorMessage);
        }
      } else {
        console.log('Camera stream already set on video element');
      }

      // Final verification before setting up canvas
      if (!this.video || this.video !== video) {
        throw new Error('Video element reference changed before canvas setup');
      }

      // Setup canvas - handle potential zero dimensions
      const videoWidth = video.videoWidth || 640;
      const videoHeight = video.videoHeight || 480;

      if (videoWidth === 0 || videoHeight === 0) {
        console.warn('Video dimensions are zero, using default dimensions');
        this.canvas.width = 640;
        this.canvas.height = 480;
      } else {
        this.canvas.width = videoWidth;
        this.canvas.height = videoHeight;
      }

      console.log(`Canvas dimensions set to ${this.canvas.width}x${this.canvas.height}`);

      // Clear any existing interval
      if (this.scanInterval) {
        clearInterval(this.scanInterval);
        this.scanInterval = null;
      }

      // Start scan interval if still active
      if (this.isActive) {
        console.log('Starting QR code scanning interval');
        this.scanInterval = window.setInterval(() => this.scan(), 200);

        // Run an immediate scan to catch any issues early
        setTimeout(() => {
          if (this.isActive) {
            this.scan();
          }
        }, 100);
      } else {
        console.warn('Scanner became inactive during initialization, not starting interval');
      }

      const setupDuration = Date.now() - this.startTime;
      console.log(`QR scanner initialization completed in ${setupDuration}ms`);

    } catch (error) {
      const setupDuration = Date.now() - this.startTime;
      console.error(`Failed to start QR scanner after ${setupDuration}ms:`, error);

      // Make sure to clean up any resources if we fail
      this.isActive = false;
      this.stop();
      throw error;
    }
  }

  /**
   * Stop scanning for QR codes and release camera resources
   */
  public stop() {
    console.log('Stopping QR scanner');

    // Mark as inactive immediately
    this.isActive = false;

    // Stop the scanning interval
    if (this.scanInterval) {
      clearInterval(this.scanInterval);
      this.scanInterval = null;
    }

    // Release camera resources
    if (this.video && this.video.srcObject) {
      try {
        const stream = this.video.srcObject as MediaStream;
        const tracks = stream.getTracks();

        if (tracks.length > 0) {
          console.log(`Stopping ${tracks.length} camera tracks`);
          tracks.forEach(track => {
            console.log(`Stopping camera track: ${track.kind}, ID: ${track.id}`);
            track.stop();
          });
        } else {
          console.log('No tracks found in video stream');
        }

        // Clear the srcObject
        this.video.srcObject = null;
        console.log('Cleared video srcObject');
      } catch (error) {
        console.error('Error stopping camera tracks:', error);
      }
    } else {
      console.log('No video element or stream to clean up');
    }

    // Release video reference
    this.video = null;

    console.log('QR scanner stopped successfully');
  }

  /**
   * Perform a single scan for QR codes in the current video frame
   */
  private scan() {
    // First check if scanner is still active
    if (!this.isActive) {
      console.log('Scanner inactive - scan aborted');
      return;
    }

    // Check if video is available
    if (!this.video) {
      console.warn('Video element is missing during scan');
      return;
    }

    // Try-catch the entire function to handle any unexpected errors
    try {
      // Verify the video element is still accessible and not detached
      let videoValid = true;
      try {
        // Just try to access some property to see if the video is still accessible
        const testAccess = this.video.id; // Any property would do

        // Check video dimensions
        if (!this.video.videoWidth || !this.video.videoHeight) {
          // Only log occasionally to avoid console spam
          if (Math.random() < 0.1) { // Log approximately 10% of the time
            console.log('Waiting for video dimensions to be available...');
          }
          videoValid = false;
        }
      } catch (videoError) {
        console.error('Error accessing video properties - element may be detached:', videoError);
        videoValid = false;
        // This could indicate the video element is detached from DOM or null
        return;
      }

      if (!videoValid) {
        return; // Skip this scan if video isn't fully initialized
      }

      // Check if video has necessary data
      if (this.video.readyState !== this.video.HAVE_ENOUGH_DATA) {
        // Not ready yet, will try again on next interval
        return;
      }

      // Verify jsQR is loaded
      const jsQR = (window as any).jsQR;
      if (!jsQR) {
        // Only log occasionally to avoid console spam
        if (Math.random() < 0.1) {
          console.warn('jsQR library not available yet, waiting for library to load');
        }
        return;
      }

      // Set up canvas with appropriate dimensions
      try {
        // Handle potential zero dimensions
        if (this.video.videoWidth === 0 || this.video.videoHeight === 0) {
          console.warn('Video dimensions are zero, using default dimensions');
          this.canvas.width = 640;
          this.canvas.height = 480;
        } else {
          // Update canvas dimensions to match video
          this.canvas.width = this.video.videoWidth;
          this.canvas.height = this.video.videoHeight;
        }
      } catch (dimensionError) {
        console.error('Error setting canvas dimensions:', dimensionError);
        return; // Skip this frame
      }

      // Final safety check before drawing
      if (!this.video || !this.isActive || this.video.readyState !== this.video.HAVE_ENOUGH_DATA) {
        console.warn('Video element became invalid before drawing to canvas');
        return;
      }

      // Draw the current video frame to the canvas
      try {
        this.context.drawImage(
          this.video, 
          0, 
          0, 
          this.canvas.width, 
          this.canvas.height
        );
      } catch (drawError) {
        console.error('Error drawing video to canvas:', drawError);

        // If this is a "Failed to execute 'drawImage'" error, the video element might be problematic
        if (drawError instanceof DOMException && drawError.message.includes('drawImage')) {
          console.error('Could not draw video to canvas. Video element may be invalid.');

          // If we get this error repeatedly, we might want to restart the scanner
          // This could be implemented as a counter that triggers after multiple failures
        }
        return; // Skip this frame
      }

      // Get the image data from the canvas for QR code processing
      let imageData;
      try {
        imageData = this.context.getImageData(
          0, 
          0, 
          this.canvas.width, 
          this.canvas.height
        );
      } catch (imageDataError) {
        // This can happen with CORS issues or if canvas is empty
        console.error('Error getting image data from canvas:', imageDataError);
        return; // Skip this frame
      }

      // Verify we have valid image data
      if (!imageData || !imageData.data || imageData.data.length === 0) {
        console.warn('Invalid image data received from canvas');
        return;
      }

      // We're still active, process the frame for QR codes
      try {
        // Process image data to look for QR codes
        const code = jsQR(
          imageData.data, 
          imageData.width, 
          imageData.height, 
          { inversionAttempts: 'attemptBoth' } // Try both regular and inverted image
        );

        // If a QR code was found, call the callback
        if (code) {
          console.log('QR code detected with data:', code.data);
          // Call the callback with the detected QR code
          this.onDetected(code);
        }
      } catch (qrError) {
        console.error('Error processing QR code:', qrError);
      }
    } catch (error) {
      console.error('Unexpected error during QR scan:', error);

      // If this is a critical error that indicates the scanner is broken
      // we might want to trigger a full reset, but let's just log for now
    }
  }
}

